// src/components/boards/KanbanBoard.js
import React, { useState } from "react";
import { v4 as uuidv4 } from "uuid";

// Initial statuses
const DEFAULT_STATUSES = [
  "backlog",
  "todo",
  "analysis",
  "inprogress",
  "blocked",
  "code review",
  "qa",
  "milestone",
  "done",
];

// Sample issues
const initialIssues = [
  { id: uuidv4(), epic: "e1", epicName: "Frontend", status: "todo", title: "Setup repo", assignee: "John", type: "Task", storyPoints: 3 },
  { id: uuidv4(), epic: "e1", epicName: "Frontend", status: "inprogress", title: "Build navbar", assignee: "Sara", type: "Story", storyPoints: 5 },
  { id: uuidv4(), epic: "e2", epicName: "Backend", status: "analysis", title: "Database schema", assignee: "Mike", type: "Task", storyPoints: 8 },
];

// Sample epics
const initialEpics = [
  { id: "e1", name: "Frontend", statuses: [...DEFAULT_STATUSES], expanded: true },
  { id: "e2", name: "Backend", statuses: [...DEFAULT_STATUSES], expanded: true },
];

export default function KanbanBoard() {
  const [issues, setIssues] = useState(initialIssues);
  const [epics, setEpics] = useState(initialEpics);

  const [activeEpic, setActiveEpic] = useState(null);
  const [activeIssue, setActiveIssue] = useState(null);

  // ===== ISSUE CRUD =====
  const addIssue = (epicId, status) => {
    const newIssue = {
      id: uuidv4(),
      epic: epicId,
      epicName: epics.find(e => e.id === epicId)?.name || "Epic",
      status,
      title: "New Issue",
      assignee: "Unassigned",
      type: "Task",
      storyPoints: 1,
    };
    setIssues([...issues, newIssue]);
  };

  const updateIssue = (id, updates) =>
    setIssues(issues.map(issue => issue.id === id ? { ...issue, ...updates } : issue));

  const deleteIssue = (id) =>
    setIssues(issues.filter(issue => issue.id !== id));

  const moveIssue = (id, newStatus) =>
    setIssues(issues.map(issue => issue.id === id ? { ...issue, status: newStatus } : issue));

  // ===== EPIC CRUD =====
  const addEpic = () => {
    const newEpic = { id: uuidv4(), name: "New Epic", statuses: [...DEFAULT_STATUSES], expanded: true };
    setEpics([...epics, newEpic]);
  };

  const updateEpic = (id, updates) =>
    setEpics(epics.map(epic => epic.id === id ? { ...epic, ...updates } : epic));

  const deleteEpic = (id) => {
    setEpics(epics.filter(e => e.id !== id));
    setIssues(issues.filter(i => i.epic !== id));
  };

  const addColumn = (epicId) => {
    setEpics(epics.map(epic =>
      epic.id === epicId ? { ...epic, statuses: [...epic.statuses, "new column"] } : epic
    ));
  };

  const renameColumn = (epicId, oldName, newName) => {
    setEpics(epics.map(epic =>
      epic.id === epicId
        ? { ...epic, statuses: epic.statuses.map(s => s === oldName ? newName : s) }
        : epic
    ));
    setIssues(issues.map(issue =>
      issue.epic === epicId && issue.status === oldName
        ? { ...issue, status: newName }
        : issue
    ));
  };

  const deleteColumn = (epicId, name) => {
    setEpics(epics.map(epic =>
      epic.id === epicId ? { ...epic, statuses: epic.statuses.filter(s => s !== name) } : epic
    ));
    setIssues(issues.map(issue =>
      issue.epic === epicId && issue.status === name
        ? { ...issue, status: "backlog" }
        : issue
    ));
  };

  // ====== RENDER ======
  return (
    <div style={styles.container}>
      <h2 style={styles.title}>Kanban Board</h2>
      <button onClick={addEpic} style={styles.addEpicBtn}>+ Add Epic</button>

      {epics.map(epic => (
        <div key={epic.id} style={styles.epic}>
          <div style={styles.epicHeader}>
            <span
              style={{ cursor: "pointer", fontWeight: "bold" }}
              onClick={() => updateEpic(epic.id, { expanded: !epic.expanded })}
            >
              {epic.expanded ? "â–¼" : "â–¶"} {epic.name}
            </span>
            <div>
              <button onClick={() => setActiveEpic(epic)} style={styles.smallBtn}>âœï¸</button>
              <button onClick={() => deleteEpic(epic.id)} style={styles.smallBtn}>ðŸ—‘</button>
            </div>
          </div>

          {epic.expanded && (
            <div style={styles.board}>
              {epic.statuses.map(status => (
                <div
                  key={status}
                  style={styles.column}
                  onDragOver={(e) => e.preventDefault()}
                  onDrop={(e) => {
                    const issueId = e.dataTransfer.getData("id");
                    moveIssue(issueId, status);
                  }}
                >
                  <div style={styles.columnHeader}>
                    <span>{status}</span>
                    <div>
                      <button onClick={() => renameColumn(epic.id, status, prompt("Rename column:", status) || status)} style={styles.smallBtn}>âœï¸</button>
                      <button onClick={() => deleteColumn(epic.id, status)} style={styles.smallBtn}>ðŸ—‘</button>
                    </div>
                  </div>

                  {issues.filter(i => i.epic === epic.id && i.status === status).map(issue => (
                    <div
                      key={issue.id}
                      draggable
                      onDragStart={(e) => e.dataTransfer.setData("id", issue.id)}
                      style={styles.card}
                      onClick={() => setActiveIssue(issue)}
                    >
                      <b>{issue.title}</b>
                      <div>{issue.assignee}</div>
                      <small>{issue.type} â€¢ {issue.storyPoints}pt</small>
                    </div>
                  ))}

                  <button onClick={() => addIssue(epic.id, status)} style={styles.addBtn}>+ Issue</button>
                </div>
              ))}
              <button onClick={() => addColumn(epic.id)} style={styles.addColumnBtn}>+ Column</button>
            </div>
          )}
        </div>
      ))}

      {/* Epic Modal */}
      {activeEpic && (
        <div style={styles.modal}>
          <div style={styles.modalContent}>
            <h3>Edit Epic</h3>
            <input
              value={activeEpic.name}
              onChange={(e) => setActiveEpic({ ...activeEpic, name: e.target.value })}
            />
            <div style={styles.modalActions}>
              <button onClick={() => { updateEpic(activeEpic.id, { name: activeEpic.name }); setActiveEpic(null); }}>Save</button>
              <button onClick={() => setActiveEpic(null)}>Cancel</button>
            </div>
          </div>
        </div>
      )}

      {/* Issue Modal */}
      {activeIssue && (
        <div style={styles.modal}>
          <div style={styles.modalContent}>
            <h3>Edit Issue</h3>
            <input
              value={activeIssue.title}
              onChange={(e) => setActiveIssue({ ...activeIssue, title: e.target.value })}
            />
            <input
              value={activeIssue.assignee}
              onChange={(e) => setActiveIssue({ ...activeIssue, assignee: e.target.value })}
            />
            <select
              value={activeIssue.type}
              onChange={(e) => setActiveIssue({ ...activeIssue, type: e.target.value })}
            >
              <option>Task</option>
              <option>Story</option>
              <option>Bug</option>
            </select>
            <input
              type="number"
              value={activeIssue.storyPoints}
              onChange={(e) => setActiveIssue({ ...activeIssue, storyPoints: Number(e.target.value) })}
            />
            <div style={styles.modalActions}>
              <button onClick={() => { updateIssue(activeIssue.id, activeIssue); setActiveIssue(null); }}>Save</button>
              <button onClick={() => { deleteIssue(activeIssue.id); setActiveIssue(null); }}>Delete</button>
              <button onClick={() => setActiveIssue(null)}>Cancel</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

// ===== Inline CSS =====
const styles = {
  container: { padding: 20, fontFamily: "Arial" },
  title: { marginBottom: 10 },
  addEpicBtn: { marginBottom: 20, padding: "6px 12px" },
  epic: { marginBottom: 30, border: "1px solid #ccc", borderRadius: 6, background: "#fafafa" },
  epicHeader: { display: "flex", justifyContent: "space-between", alignItems: "center", padding: 10, background: "#eee" },
  board: { display: "flex", gap: 10, padding: 10, overflowX: "auto" },
  column: { minWidth: 200, background: "#f4f4f4", borderRadius: 6, padding: 10 },
  columnHeader: { display: "flex", justifyContent: "space-between", alignItems: "center", marginBottom: 8 },
  card: { background: "white", border: "1px solid #ccc", borderRadius: 4, padding: 8, marginBottom: 8, cursor: "grab" },
  addBtn: { width: "100%", padding: 6, marginTop: 5, background: "#ddd", border: "none", borderRadius: 4, cursor: "pointer" },
  addColumnBtn: { minWidth: 150, padding: 10, marginLeft: 10, background: "#cce5ff", border: "none", borderRadius: 6, cursor: "pointer" },
  smallBtn: { marginLeft: 5, border: "none", background: "transparent", cursor: "pointer" },
  modal: { position: "fixed", top: 0, left: 0, right: 0, bottom: 0, background: "rgba(0,0,0,0.5)", display: "flex", alignItems: "center", justifyContent: "center" },
  modalContent: { background: "white", padding: 20, borderRadius: 6, minWidth: 300 },
  modalActions: { marginTop: 10, display: "flex", gap: 10 },
};
