import React, { useState, useRef } from "react";
import { useDrag, useDrop, DndProvider } from "react-dnd";
import { HTML5Backend } from "react-dnd-html5-backend";
import styled from "styled-components";
import { format, addDays, differenceInDays, parseISO, isBefore } from "date-fns";
import Xarrow from "react-xarrows";
import html2canvas from "html2canvas";
import jsPDF from "jspdf";
import * as XLSX from "xlsx";

// Styled components
const Container = styled.div`
  padding: 20px;
  font-family: Arial;
`;

const TimelineContainer = styled.div`
  border: 1px solid #ccc;
  padding: 10px;
  overflow-x: auto;
  position: relative;
  height: 450px;
`;

const ZoomControls = styled.div`
  margin-bottom: 10px;
`;

const FilterControls = styled.div`
  margin-bottom: 10px;
`;

const TaskBar = styled.div`
  position: absolute;
  height: ${(props) => (props.milestone ? "20px" : "30px")};
  border-radius: 4px;
  color: white;
  padding: 0 5px;
  font-size: 12px;
  display: flex;
  align-items: center;
  cursor: ${(props) => (props.canEdit ? "grab" : "not-allowed")};
  border: ${(props) => (props.conflict ? "2px solid red" : "none")};
  opacity: ${(props) => (props.isDragging ? 0.5 : 1)};
  background-color: ${(props) => props.color};
`;

// Task component with drag, milestone, role-based editing, and conflict
function Task({ task, dates, onTaskMove, canEdit, conflict }) {
  const startIdx = dates.findIndex((d) => d === task.start);
  const endIdx = dates.findIndex((d) => d === task.end);
  const width = (endIdx - startIdx + 1) * 100;

  const [{ isDragging }, drag] = useDrag(() => ({
    type: "TASK",
    item: { id: task.id },
    canDrag: canEdit && !task.milestone, // Milestones cannot be dragged
    collect: (monitor) => ({
      isDragging: !!monitor.isDragging(),
    }),
  }));

  const [, drop] = useDrop({
    accept: "TASK",
    hover: (item, monitor) => {
      if (!canEdit || task.milestone) return;
      const delta = monitor.getDifferenceFromInitialOffset();
      const daysMoved = Math.round(delta.x / 100);
      if (daysMoved !== 0) {
        onTaskMove(item.id, daysMoved);
      }
    },
  });

  return (
    <TaskBar
      ref={(node) => drag(drop(node))}
      style={{
        left: startIdx * 100,
        width: task.milestone ? 20 : width,
        top: task.id * 50,
      }}
      id={`task-${task.id}`}
      isDragging={isDragging}
      milestone={task.milestone}
      canEdit={canEdit}
      conflict={conflict}
      color={task.color}
    >
      {task.milestone && "ðŸŽ¯"} {task.title} {task.progress ? `(${task.progress}%)` : ""}
    </TaskBar>
  );
}

// Main timeline component
function TimelineJiraAdvanced() {
  const initialTasks = [
    {
      id: 1,
      title: "Design UI",
      start: "2025-09-22",
      end: "2025-09-25",
      progress: 50,
      color: "#0052CC",
      assignee: "John",
      dependencies: [2],
      milestone: false,
    },
    {
      id: 2,
      title: "Develop Backend",
      start: "2025-09-23",
      end: "2025-09-28",
      progress: 20,
      color: "#FF5630",
      assignee: "Alice",
      dependencies: [],
      milestone: true,
    },
    {
      id: 3,
      title: "QA Testing",
      start: "2025-09-26",
      end: "2025-10-02",
      progress: 0,
      color: "#36B37E",
      assignee: "John",
      dependencies: [1, 2],
      milestone: false,
    },
  ];

  const [tasks, setTasks] = useState(initialTasks);
  const [zoom, setZoom] = useState("week");
  const [filterAssignee, setFilterAssignee] = useState("All");
  const [userRole, setUserRole] = useState("editor"); // "viewer" or "editor"
  const timelineRef = useRef();

  const startDate = parseISO("2025-09-20");
  const endDate = parseISO("2025-10-05");
  const totalDays = differenceInDays(endDate, startDate) + 1;

  const dates = [];
  for (let i = 0; i < totalDays; i++) {
    dates.push(format(addDays(startDate, i), "yyyy-MM-dd"));
  }

  // Move task by delta days
  const handleTaskMove = (id, deltaDays) => {
    setTasks((prev) =>
      prev.map((task) => {
        if (task.id === id) {
          const oldStart = parseISO(task.start);
          const oldEnd = parseISO(task.end);
          let newStart = addDays(oldStart, deltaDays);
          let newEnd = addDays(oldEnd, deltaDays);

          if (isBefore(newStart, startDate)) {
            newStart = startDate;
            newEnd = addDays(startDate, differenceInDays(oldEnd, oldStart));
          }
          if (isBefore(endDate, newEnd)) {
            newEnd = endDate;
            newStart = addDays(endDate, -differenceInDays(oldEnd, oldStart));
          }

          return {
            ...task,
            start: format(newStart, "yyyy-MM-dd"),
            end: format(newEnd, "yyyy-MM-dd"),
          };
        }
        return task;
      })
    );
  };

  // Filtering
  const filteredTasks =
    filterAssignee === "All"
      ? tasks
      : tasks.filter((t) => t.assignee === filterAssignee);

  const uniqueAssignees = ["All", ...new Set(tasks.map((t) => t.assignee))];

  // Detect conflicts
  const conflicts = {};
  tasks.forEach((taskA) => {
    tasks.forEach((taskB) => {
      if (
        taskA.id !== taskB.id &&
        taskA.assignee === taskB.assignee &&
        !(parseISO(taskA.end) < parseISO(taskB.start) || parseISO(taskB.end) < parseISO(taskA.start))
      ) {
        conflicts[taskA.id] = true;
        conflicts[taskB.id] = true;
      }
    });
  });

  // Export PDF
  const exportPDF = () => {
    html2canvas(timelineRef.current).then((canvas) => {
      const imgData = canvas.toDataURL("image/png");
      const pdf = new jsPDF("l", "pt", [canvas.width, canvas.height]);
      pdf.addImage(imgData, "PNG", 0, 0);
      pdf.save("timeline.pdf");
    });
  };

  // Export Excel
  const exportExcel = () => {
    const ws = XLSX.utils.json_to_sheet(
      tasks.map((t) => ({
        Title: t.title,
        Start: t.start,
        End: t.end,
        Progress: t.progress,
        Assignee: t.assignee,
        Milestone: t.milestone ? "Yes" : "No",
      }))
    );
    const wb = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(wb, ws, "Timeline");
    XLSX.writeFile(wb, "timeline.xlsx");
  };

  // Import CSV / XLSX
  const handleImport = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    const reader = new FileReader();
    reader.onload = (evt) => {
      const data = new Uint8Array(evt.target.result);
      const workbook = XLSX.read(data, { type: "array" });
      const sheetName = workbook.SheetNames[0];
      const sheet = workbook.Sheets[sheetName];
      const importedTasks = XLSX.utils.sheet_to_json(sheet).map((t, idx) => ({
        id: tasks.length + idx + 1,
        title: t.Title,
        start: t.Start,
        end: t.End,
        progress: t.Progress || 0,
        assignee: t.Assignee || "Unknown",
        milestone: t.Milestone === "Yes",
        color: "#888888",
        dependencies: [],
      }));
      setTasks((prev) => [...prev, ...importedTasks]);
    };
    reader.readAsArrayBuffer(file);
  };

  return (
    <DndProvider backend={HTML5Backend}>
      <Container>
        <h1>Advanced Jira-like Timeline</h1>
        <ZoomControls>
          Zoom:
          <select value={zoom} onChange={(e) => setZoom(e.target.value)}>
            <option value="day">Day</option>
            <option value="week">Week</option>
            <option value="month">Month</option>
            <option value="quarter">Quarter</option>
          </select>
          Role:
          <select
            value={userRole}
            onChange={(e) => setUserRole(e.target.value)}
            style={{ marginLeft: "10px" }}
          >
            <option value="viewer">Viewer</option>
            <option value="editor">Editor</option>
          </select>
        </ZoomControls>
        <FilterControls>
          Filter by Assignee:
          <select
            value={filterAssignee}
            onChange={(e) => setFilterAssignee(e.target.value)}
          >
            {uniqueAssignees.map((assignee) => (
              <option key={assignee} value={assignee}>
                {assignee}
              </option>
            ))}
          </select>
          <input
            type="file"
            accept=".csv,.xlsx"
            onChange={handleImport}
            style={{ marginLeft: "10px" }}
          />
        </FilterControls>
        <button onClick={exportPDF}>Export PDF</button>
        <button onClick={exportExcel} style={{ marginLeft: "10px" }}>
          Export Excel
        </button>
        <TimelineContainer ref={timelineRef}>
          <div style={{ display: "flex", borderBottom: "1px solid #ccc" }}>
            {dates.map((date) => (
              <div
                key={date}
                style={{
                  minWidth: "100px",
                  borderRight: "1px solid #eee",
                  textAlign: "center",
                }}
              >
                {date}
              </div>
            ))}
          </div>
          {filteredTasks.map((task) => (
            <Task
              key={task.id}
              task={task}
              dates={dates}
              onTaskMove={handleTaskMove}
              canEdit={userRole === "editor"}
              conflict={conflicts[task.id]}
            />
          ))}
          {/* Dependency arrows */}
          {filteredTasks.map((task) =>
            task.dependencies.map((depId) => {
              const depTask = tasks.find((t) => t.id === depId);
              if (!depTask) return null;
              return (
                <Xarrow
                  key={`${task.id}-${depId}`}
                  start={`task-${depTask.id}`}
                  end={`task-${task.id}`}
                  color="black"
                  strokeWidth={2}
                  headSize={4}
                />
              );
            })
          )}
        </TimelineContainer>
      </Container>
    </DndProvider>
  );
}

export default TimelineJiraAdvanced;
