    outline: none;
    border-color: #1976d2;
  }
  .swimlane-count {
    color: #5e6c84;
    font-size: 12px;
    font-weight: normal;
  }
  .col-header {
    display: flex;
    align-items: center;
    margin-bottom: 4px;
    gap: 6px;
  }
  .col-title {
    font-weight: 600;
    font-size: 12px;
    color: #5e6c84;
    text-transform: uppercase;
    letter-spacing: 0.5px;
    flex-grow: 1;
  }
  .col-count {
    background: #dfe5e5;
    color: #5e6c84;
    border-radius: 12px;
    padding: 2px 8px;
    font-size: 11px;
    font-weight: 600;
    min-width: 20px;
    text-align: center;
  }
  .col-icons {
    display: flex;
    gap: 6px;
  }
  .col-icon {
    cursor: pointer;
    color: #5e6c84;
    font-size: 18px;
    background: none;
    border: none;
    padding: 0;
