      jsx: true,
      children: `
        /* Existing Styles */
        .users-container {
          min-height: 100vh;
          background: #f7f9fd; /* Lighter background */
          padding: 20px;
        }

        .users-header {
          text-align: center;
          margin-bottom: 40px;
          color: #2d3748;
        }

        .users-title {
          font-size: 2.5rem;
          font-weight: 700;
          margin-bottom: 8px;
        }

        .users-subtitle {
          font-size: 1.1rem;
          opacity: 0.8;
          color: #4a5568;
        }

        .error-message {
            color: #e53e3e;
            background: #fed7d7;
            padding: 8px 15px;
            border-radius: 8px;
            margin-top: 15px;
            display: inline-block;
            font-weight: 500;
        }

        .users-grid {
          display: grid;
          grid-template-columns: 1fr;
          gap: 30px;
          max-width: 1400px;
          margin: 0 auto;
        }

        @media (min-width: 1024px) {
          .users-grid {
            grid-template-columns: 450px 1fr; /* Increased form size */
          }
        }

        .glassmorphism {
          background: rgba(255, 255, 255, 0.95);
          backdrop-filter: blur(10px);
          border-radius: 20px;
          border: 1px solid rgba(255, 255, 255, 0.2);
          box-shadow: 0 8px 32px rgba(0, 0, 0, 0.05); /* Lighter shadow */
        }

        .users-form-card,
        .users-list-card {
          padding: 30px;
        }

        .card-header {
          display: flex;
          justify-content: space-between;
          align-items: center;
          margin-bottom: 30px;
          flex-wrap: wrap;
          gap: 15px;
        }

        .card-title {
          font-size: 1.5rem;
          font-weight: 600;
          color: #2d3748;
          display: flex;
          align-items: center;
        }

        .icon-spacing {
          margin-right: 10px;
        }

        /* NEW/UPDATED Styles */
        .form-grid-expanded {
          display: grid;
          grid-template-columns: 1fr 1fr; /* Two columns for a denser form */
          gap: 20px;
          margin-bottom: 30px;
        }

        .input-group {
          display: flex;
          flex-direction: column;
        }

        .input-label {
          font-weight: 500;
          margin-bottom: 8px;
          color: #4a5568;
        }

        /* Checkbox and File Upload Styles */
        .checkbox-group {
            grid-column: span 1;
        }

        .checkbox-label {
            display: flex;
            align-items: center;
            font-weight: 400;
            color: #4a5568;
            cursor: pointer;
            padding: 10px;
            background: #edf2f7;
            border-radius: 8px;
        }

        .checkbox-label input[type="checkbox"] {
            margin-right: 10px;
            width: 18px;
            height: 18px;
            accent-color: #667eea;
        }

        .file-group input[type="file"] {
          display: none;
        }

        .file-upload-btn {
          padding: 12px 16px;
          border: 2px solid #667eea;
          border-radius: 10px;
          font-size: 1rem;
          font-weight: 500;
          color: #667eea;
          background: #ebf4ff;
          cursor: pointer;
          transition: background-color 0.2s;
          display: flex;
          align-items: center;
          justify-content: flex-start;
        }

        .file-upload-btn:hover {
          background: #d4e3ff;
        }

        .file-upload-btn .btn-icon {
          margin-right: 8px;
          font-size: 1.2rem;
        }
        /* End New Styles */


        .modern-input,
        .modern-select {
          padding: 12px 16px;
          border: 2px solid #e2e8f0;
          border-radius: 10px;
          font-size: 1rem;
          transition: all 0.3s ease;
          background: white;
          width: 100%;
          box-sizing: border-box;
        }

        .modern-input:focus,
        .modern-select:focus {
          outline: none;
          border-color: #667eea;
          box-shadow: 0 0 0 3px rgba(102, 126, 234, 0.1);
        }

        .create-btn {
          grid-column: 1 / -1; /* Make button span both columns */
          width: 100%;
          padding: 14px;
          background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
          color: white;
          border: none;
          border-radius: 10px;
          font-size: 1rem;
          font-weight: 600;
          cursor: pointer;
          transition: transform 0.2s ease;
          display: flex;
          align-items: center;
          justify-content: center;
        }

        .create-btn:hover {
          transform: translateY(-2px);
          box-shadow: 0 4px 12px rgba(102, 126, 234, 0.4);
        }

        .btn-icon {
          margin-right: 8px;
        }

        .search-container {
          position: relative;
          min-width: 250px;
        }

        .search-input {
          padding: 12px 16px 12px 40px;
          border: 2px solid #e2e8f0;
          border-radius: 10px;
          font-size: 1rem;
          width: 100%;
          transition: all 0.3s ease;
        }

        .search-input:focus {
          outline: none;
          border-color: #667eea;
          box-shadow: 0 0 0 3px rgba(102, 126, 234, 0.1);
        }

        .search-icon {
          position: absolute;
          left: 12px;
          top: 50%;
          transform: translateY(-50%);
          color: #a0aec0;
        }

        .table-container {
          overflow-x: auto;
          border-radius: 10px;
        }

        .modern-table {
          width: 100%;
          border-collapse: collapse;
          min-width: 800px; /* Increased min-width for new columns */
        }

        .modern-table th {
          background: #f7fafc;
          padding: 16px;
          text-align: left;
          font-weight: 600;
          color: #4a5568;
          border-bottom: 2px solid #e2e8f0;
        }

        .modern-table td {
          padding: 16px;
          border-bottom: 1px solid #e2e8f0;
        }

        .modern-table tr:hover {
          background: #f7fafc;
        }

        .editing-row {
          background: #fff9e6 !important;
        }

        .user-info {
          display: flex;
          align-items: center;
          gap: 12px;
        }

        .file-indicator {
            font-size: 1.1em;
            cursor: help;
        }

        .avatar {
          width: 40px;
          height: 40px;
          border-radius: 50%;
          background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
          display: flex;
          align-items: center;
          justify-content: center;
          color: white;
          font-weight: 600;
          font-size: 1.1rem;
          flex-shrink: 0;
        }

        /* New Status Dot */
        .status-dot {
            height: 10px;
            width: 10px;
            border-radius: 50%;
            display: inline-block;
            margin-right: 5px;
        }

        .status-active {
            background-color: #48bb78; /* Green */
        }

        .status-inactive {
            background-color: #e53e3e; /* Red */
        }
        /* End New Status Dot */


        .role-badge {
          padding: 6px 12px;
          border-radius: 20px;
          font-size: 0.85rem;
          font-weight: 500;
          white-space: nowrap; /* Prevents wrapping */
        }

        .role-admin {
          background: #fed7d7;
          color: #c53030;
        }

        .role-manager {
          background: #feebc8;
          color: #d69e2e;
        }

        .role-developer, .role-engineering {
          background: #c6f6d5;
          color: #276749;
        }

        .role-reporter {
          background: #e9d8fd;
          color: #6b46c1;
        }

        /* New Department Badges */
        .role-sales {
            background: #bee3f8;
            color: #2c5282;
        }

        .role-hr {
            background: #fff0f5;
            color: #d53f8c;
        }

        .role-marketing {
            background: #faf089;
            color: #744210;
        }
        /* End New Department Badges */


        .action-buttons {
          display: flex;
          gap: 8px;
        }

        .btn-edit,
        .btn-delete,
        .btn-success,
        .btn-cancel {
          padding: 8px;
          border: none;
          border-radius: 8px;
          cursor: pointer;
          transition: all 0.2s ease;
          display: flex;
          align-items: center;
          justify-content: center;
        }

        .btn-edit {
          background: #e6fffa;
          color: #234e52;
        }

        .btn-edit:hover {
          background: #b2f5ea;
        }

        .btn-delete {
          background: #fed7d7;
          color: #c53030;
        }

        .btn-delete:hover {
          background: #feb2b2;
        }

        .btn-success {
          background: #c6f6d5;
          color: #276749;
        }

        .btn-success:hover {
          background: #9ae6b4;
        }

        .btn-cancel {
          background: #fed7d7;
          color: #c53030;
        }

        .btn-cancel:hover {
          background: #feb2b2;
        }

        .edit-input,
        .edit-select {
          padding: 8px 12px;
          border: 2px solid #e2e8f0;
          border-radius: 6px;
          width: 100%;
          font-size: 0.9rem;
          box-sizing: border-box;
        }

        .edit-input:focus,
        .edit-select:focus {
          outline: none;
          border-color: #667eea;
        }

        .empty-state {
          text-align: center;
          padding: 40px;
          color: #a0aec0;
          font-style: italic;
        }

        /* Media Queries */
        @media (max-width: 768px) {
          .users-container {
            padding: 10px;
          }

          .users-title {
            font-size: 2rem;
          }

          .users-form-card,
          .users-list-card {
            padding: 20px;
          }

          .form-grid-expanded {
              grid-template-columns: 1fr; /* Single column layout for small screens */
          }

          .card-header {
            flex-direction: column;
            align-items: stretch;
          }

          .search-container {
            min-width: auto;
          }

          .modern-table {
            font-size: 0.85rem;
            min-width: 600px;
          }

          .modern-table th,
          .modern-table td {
            padding: 10px 6px;
          }

          .action-buttons {
            flex-direction: row; /* Keep action buttons inline on smaller screens */
          }
