import React, { useEffect, useState } from 'react';

export default function ForYou() {
  const [user, setUser] = useState({ name: "John Doe", id: 1 });
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
  const [particles, setParticles] = useState([]);
  const [orbs, setOrbs] = useState([]);

  // Recently viewed boards data
  const [recentlyViewed] = useState([
    { id: 1, title: "Website Redesign", team: "Design Team", lastViewed: "2 hours ago", color: "#8B5CF6" },
    { id: 2, title: "Q4 Product Launch", team: "Product Team", lastViewed: "1 day ago", color: "#3B82F6" },
    { id: 3, title: "User Feedback", team: "Research Team", lastViewed: "3 days ago", color: "#10B981" }
  ]);

  // Your boards data
  const [yourBoards] = useState([
    { id: 1, title: "Development Backlog", items: 12, updated: "Today", color: "#EF4444" },
    { id: 2, title: "Marketing Campaign", items: 8, updated: "Yesterday", color: "#F59E0B" },
    { id: 3, title: "Bug Fixes", items: 5, updated: "2 days ago", color: "#06B6D4" },
    { id: 4, title: "Feature Requests", items: 15, updated: "1 week ago", color: "#84CC16" }
  ]);

  useEffect(() => {
    // Create floating particles
    const newParticles = Array.from({ length: 12 }, (_, i) => ({
      id: i,
      size: Math.random() * 2 + 1,
      left: Math.random() * 100,
      animationDelay: Math.random() * 20,
      duration: Math.random() * 25 + 20,
      opacity: Math.random() * 0.3 + 0.1
    }));
    setParticles(newParticles);

    // Create floating orbs
    const newOrbs = Array.from({ length: 4 }, (_, i) => ({
      id: i,
      size: Math.random() * 100 + 60,
      left: Math.random() * 100,
      top: Math.random() * 100,
      animationDelay: Math.random() * 10,
      duration: Math.random() * 30 + 25
    }));
    setOrbs(newOrbs);

    // Mouse move handler
    const handleMouseMove = (e) => {
      setMousePosition({
        x: (e.clientX / window.innerWidth) * 100,
        y: (e.clientY / window.innerHeight) * 100
      });
    };

    window.addEventListener('mousemove', handleMouseMove);
    return () => window.removeEventListener('mousemove', handleMouseMove);
  }, []);

  const styles = {
    container: {
      minHeight: "100vh",
      backgroundColor: "#0a0a0f",
      background: `
        radial-gradient(circle at 25% 75%, rgba(120, 119, 198, 0.15) 0%, transparent 50%),
        radial-gradient(circle at 75% 25%, rgba(255, 119, 198, 0.15) 0%, transparent 50%),
        radial-gradient(circle at 50% 50%, rgba(120, 219, 255, 0.1) 0%, transparent 50%),
        linear-gradient(135deg, #0a0a0f 0%, #1a1a2e 25%, #16213e 50%, #0a0a0f 100%)
      `,
      padding: "2rem",
      fontFamily: "'Inter', -apple-system, BlinkMacSystemFont, sans-serif",
      position: "relative",
      overflow: "hidden"
    },
    backgroundOverlay: {
      position: "absolute",
      top: 0,
      left: 0,
      width: "100%",
      height: "100%",
      background: `
        radial-gradient(circle at ${mousePosition.x}% ${mousePosition.y}%, rgba(59, 130, 246, 0.06) 0%, transparent 50%)
      `,
      transition: "background 0.3s ease",
      pointerEvents: "none"
    },
    particlesContainer: {
      position: "absolute",
      top: 0,
      left: 0,
      width: "100%",
      height: "100%",
      pointerEvents: "none",
      zIndex: 1
    },
    particle: {
      position: "absolute",
      background: "linear-gradient(45deg, #60a5fa, #a855f7)",
      borderRadius: "50%",
      animation: "floatParticles 30s infinite linear",
      filter: "blur(1px)"
    },
    orb: {
      position: "absolute",
      borderRadius: "50%",
      filter: "blur(60px)",
      animation: "floatOrbs 35s infinite ease-in-out alternate",
      opacity: 0.2
    },
    meshGradient: {
      position: "absolute",
      top: 0,
      left: 0,
      width: "100%",
      height: "100%",
      background: `
        conic-gradient(from 180deg at 50% 50%, 
          rgba(59, 130, 246, 0.03) 0deg,
          rgba(168, 85, 247, 0.03) 60deg,
          rgba(236, 72, 153, 0.03) 120deg,
          rgba(59, 130, 246, 0.03) 180deg,
          rgba(34, 197, 94, 0.03) 240deg,
          rgba(59, 130, 246, 0.03) 360deg
        )
      `,
      animation: "rotateMesh 50s linear infinite",
      opacity: 0.3,
      pointerEvents: "none"
    },
    content: {
      position: "relative",
      zIndex: 10,
      maxWidth: "1200px",
      margin: "0 auto"
    },
    welcomeTitle: {
      fontSize: "2.5rem",
      fontWeight: "700",
      color: "#F1F5F9",
      margin: "0 0 0.5rem 0",
      animation: "fadeInUp 1s ease-out"
    },
    subtitle: {
      color: "#94A3B8",
      fontSize: "1.1rem",
      fontWeight: "400",
      margin: "0 0 3rem 0",
      animation: "fadeInUp 1.2s ease-out"
    },
    cardsContainer: {
      display: "grid",
      gridTemplateColumns: "1fr 1fr",
      gap: "2rem",
      marginBottom: "2rem"
    },
    card: {
      background: "rgba(15, 15, 25, 0.7)",
      backdropFilter: "blur(25px)",
      borderRadius: "24px",
      padding: "2.5rem",
      border: "1px solid rgba(255, 255, 255, 0.08)",
      boxShadow: `
        0 25px 50px -12px rgba(0, 0, 0, 0.4),
        inset 0 1px 0 rgba(255, 255, 255, 0.1)
      `,
      position: "relative",
      animation: "fadeInUp 1.4s ease-out"
    },
    cardGlow: {
      position: "absolute",
      top: 0,
      left: 0,
      right: 0,
      height: "2px",
      background: "linear-gradient(90deg, transparent, #60a5fa, #a855f7, transparent)",
      animation: "cardGlow 5s ease-in-out infinite",
      borderRadius: "24px 24px 0 0"
    },
    cardHeader: {
      display: "flex",
      justifyContent: "space-between",
      alignItems: "center",
      marginBottom: "2rem"
    },
    cardTitle: {
      fontSize: "1.5rem",
      fontWeight: "700",
      color: "#F1F5F9",
      margin: "0"
    },
    boardList: {
      listStyle: "none",
      padding: "0",
      margin: "0",
      display: "flex",
      flexDirection: "column",
      gap: "1rem"
    },
    boardItem: {
      padding: "1.25rem",
      background: "rgba(255, 255, 255, 0.03)",
      borderRadius: "16px",
      border: "1px solid rgba(255, 255, 255, 0.06)",
      transition: "all 0.3s cubic-bezier(0.4, 0, 0.2, 1)",
      cursor: "pointer"
    },
    boardTitle: {
      fontSize: "1rem",
      fontWeight: "600",
      color: "#F1F5F9",
      margin: "0 0 0.5rem 0",
      display: "flex",
      alignItems: "center",
      gap: "0.5rem"
    },
    boardMeta: {
      display: "flex",
      justifyContent: "space-between",
      alignItems: "center",
      fontSize: "0.8rem",
      color: "#94A3B8"
    },
    teamBadge: {
      padding: "0.2rem 0.6rem",
      borderRadius: "8px",
      fontSize: "0.7rem",
      fontWeight: "500",
      background: "rgba(255, 255, 255, 0.1)"
    },
    itemsCount: {
      padding: "0.2rem 0.6rem",
      borderRadius: "8px",
      fontSize: "0.7rem",
      fontWeight: "500",
      background: "rgba(59, 130, 246, 0.2)",
      color: "#60A5FA"
    },
    colorIndicator: {
      width: "4px",
      height: "20px",
      borderRadius: "2px"
    },
    emptyState: {
      textAlign: "center",
      color: "#64748B",
      padding: "3rem 1rem",
      fontSize: "1rem"
    }
  };

  return (
    <div style={styles.container}>
      {/* Background Effects */}
      <div style={styles.backgroundOverlay} />
      <div style={styles.meshGradient} />

      {/* Floating Orbs */}
      <div style={styles.particlesContainer}>
        {orbs.map(orb => (
          <div
            key={`orb-${orb.id}`}
            style={{
              ...styles.orb,
              width: `${orb.size}px`,
              height: `${orb.size}px`,
              left: `${orb.left}%`,
              top: `${orb.top}%`,
              background: orb.id % 3 === 0 
                ? "radial-gradient(circle, #60a5fa, #3b82f6)"
                : orb.id % 3 === 1 
                ? "radial-gradient(circle, #a855f7, #8b5cf6)"
                : "radial-gradient(circle, #ec4899, #f43f5e)",
              animationDelay: `${orb.animationDelay}s`,
              animationDuration: `${orb.duration}s`
            }}
          />
        ))}
      </div>

      {/* Animated Particles */}
      <div style={styles.particlesContainer}>
        {particles.map(particle => (
          <div
            key={particle.id}
            style={{
              ...styles.particle,
              width: `${particle.size}px`,
              height: `${particle.size}px`,
              left: `${particle.left}%`,
              top: "-20px",
              opacity: particle.opacity,
              animationDelay: `${particle.animationDelay}s`,
              animationDuration: `${particle.duration}s`
            }}
          />
        ))}
      </div>

      <div style={styles.content}>
        {/* Header */}
        <h1 style={styles.welcomeTitle}>üëã Welcome, {user?.name}</h1>
        <p style={styles.subtitle}>Your workspace at a glance</p>

        {/* Two Column Layout */}
        <div style={styles.cardsContainer}>
          {/* Recently Viewed Card */}
          <div style={styles.card}>
            <div style={styles.cardGlow} />
            <div style={styles.cardHeader}>
              <h2 style={styles.cardTitle}>üïí Recently Viewed</h2>
            </div>
            <ul style={styles.boardList}>
              {recentlyViewed.length > 0 ? (
                recentlyViewed.map((board) => (
                  <li key={board.id} style={styles.boardItem}>
                    <div style={styles.boardTitle}>
                      <div 
                        style={{
                          ...styles.colorIndicator,
                          backgroundColor: board.color
                        }} 
                      />
                      {board.title}
                    </div>
                    <div style={styles.boardMeta}>
                      <span style={styles.teamBadge}>{board.team}</span>
                      <span>Viewed {board.lastViewed}</span>
                    </div>
                  </li>
                ))
              ) : (
                <li style={styles.emptyState}>No recently viewed boards</li>
              )}
            </ul>
          </div>

          {/* Your Boards Card */}
          <div style={styles.card}>
            <div style={styles.cardGlow} />
            <div style={styles.cardHeader}>
              <h2 style={styles.cardTitle}>üóÇÔ∏è Your Boards</h2>
            </div>
            <ul style={styles.boardList}>
              {yourBoards.length > 0 ? (
                yourBoards.map((board) => (
                  <li key={board.id} style={styles.boardItem}>
                    <div style={styles.boardTitle}>
                      <div 
                        style={{
                          ...styles.colorIndicator,
                          backgroundColor: board.color
                        }} 
                      />
                      {board.title}
                    </div>
                    <div style={styles.boardMeta}>
                      <span style={styles.itemsCount}>{board.items} items</span>
                      <span>Updated {board.updated}</span>
                    </div>
                  </li>
                ))
              ) : (
                <li style={styles.emptyState}>No boards created yet</li>
              )}
            </ul>
          </div>
        </div>
      </div>

      {/* CSS Animations */}
      <style>
        {`
          @keyframes floatParticles {
            0% {
              transform: translateY(0) rotate(0deg);
              opacity: 0;
            }
            10% {
              opacity: 1;
            }
            90% {
              opacity: 1;
            }
            100% {
              transform: translateY(100vh) rotate(360deg);
              opacity: 0;
            }
          }

          @keyframes floatOrbs {
            0% {
              transform: translateY(0px) translateX(0px) scale(1);
            }
            50% {
              transform: translateY(-20px) translateX(15px) scale(1.02);
            }
            100% {
              transform: translateY(-5px) translateX(-10px) scale(0.98);
            }
          }

          @keyframes rotateMesh {
            0% {
              transform: rotate(0deg);
            }
            100% {
              transform: rotate(360deg);
            }
          }

          @keyframes cardGlow {
            0%, 100% {
              opacity: 0.4;
              transform: scaleX(0.9);
            }
            50% {
              opacity: 0.8;
              transform: scaleX(1);
            }
          }

          @keyframes fadeInUp {
            0% {
              opacity: 0;
              transform: translateY(20px);
            }
            100% {
              opacity: 1;
              transform: translateY(0);
            }
          }

          .board-item:hover {
            background: rgba(255, 255, 255, 0.05) !important;
            border-color: rgba(96, 165, 250, 0.2) !important;
            transform: translateX(4px) !important;
          }
        `}
      </style>
    </div>
  );
}
