import React, { useEffect, useState } from "react";
import { listAssets, addAsset } from "../../services/mockApi";
import { v4 as uuidv4 } from "uuid";

export default function AssetsBoard() {
  const [assets, setAssets] = useState([]);
  const [form, setForm] = useState({ name: "", type: "Laptop", owner: "" });
  const [editingId, setEditingId] = useState(null);
  const [editFields, setEditFields] = useState({});
  const [hoveredId, setHoveredId] = useState(null);

  useEffect(() => {
    listAssets().then(setAssets);
  }, []);

  // Add asset handler for Add Form
  const add = async () => {
    const assetName = form.name;
    const assetType = form.type || "Laptop";
    const assetOwner = form.owner || "";
    if (!assetName?.trim()) return;

    const newAsset = {
      id: uuidv4(),
      name: assetName,
      type: assetType,
      owner: assetOwner,
      // Removed status field as per requirement
    };
    await addAsset(newAsset);
    setAssets(await listAssets());
    setForm({ name: "", type: "Laptop", owner: "" });
  };

  // Edit asset inline
  const startEditing = asset => {
    setEditingId(asset.id);
    setEditFields({ ...asset });
  };
  const saveEdit = async id => {
    setAssets(assets.map(a => (a.id === id ? { ...a, ...editFields } : a)));
    setEditingId(null);
  };
  const cancelEdit = () => setEditingId(null);

  // Delete asset handler
  const deleteAsset = async id => {
    setAssets(assets.filter(a => a.id !== id));
  };

  return (
    <div
      style={{
        padding: "2rem",
        fontFamily: "Arial, sans-serif",
        background: "#F4F5F7",
        minHeight: "100vh",
      }}
    >
      {/* Add Asset Top Form */}
      <div
        style={{
          marginBottom: "2rem",
          background: "#fff",
          padding: "1.5rem",
          borderRadius: "8px",
          boxShadow: "0 2px 8px rgba(0,0,0,0.1)",
        }}
      >
        <h2 style={{ marginBottom: "1rem", fontSize: "1.5rem" }}>
          Add User Asset
        </h2>
        <div style={{ display: "flex", gap: "1rem", flexWrap: "wrap" }}>
          <input
            placeholder="Asset Name"
            value={form.name}
            onChange={e => setForm(f => ({ ...f, name: e.target.value }))}
            style={{
              flex: 1,
              padding: "0.5rem",
              borderRadius: "4px",
              border: "1px solid #ccc",
            }}
          />
          <input
            placeholder="Type"
            value={form.type}
            onChange={e => setForm(f => ({ ...f, type: e.target.value }))}
            style={{
              flex: 1,
              padding: "0.5rem",
              borderRadius: "4px",
              border: "1px solid #ccc",
            }}
          />
          <input
            placeholder="Owner"
            value={form.owner}
            onChange={e => setForm(f => ({ ...f, owner: e.target.value }))}
            style={{
              flex: 1,
              padding: "0.5rem",
              borderRadius: "4px",
              border: "1px solid #ccc",
            }}
          />
          <button
            onClick={add}
            style={{
              padding: "0.5rem 1rem",
              background: "#0052CC",
              color: "#fff",
              border: "none",
              borderRadius: "4px",
              cursor: "pointer",
            }}
          >
            Add
          </button>
        </div>
      </div>

      {/* User Asset List */}
      <div
        style={{
          background: "#EBECF0",
          padding: "1rem",
          borderRadius: "8px",
          minHeight: "300px",
        }}
      >
        <h3 style={{ color: "#0052CC" }}>User Assets</h3>
        {assets.length === 0 && (
          <div style={{ color: "#999", marginTop: "2rem" }}>
            No assets found for the user.
          </div>
        )}

        {assets.map(a => (
          <div
            key={a.id}
            onMouseEnter={() => setHoveredId(a.id)}
            onMouseLeave={() => setHoveredId(null)}
            style={{
              padding: "0.75rem 1rem",
              marginBottom: "0.5rem",
              borderRadius: "6px",
              background: "#fff",
              position: "relative",
              boxShadow: "0 2px 8px rgba(0,0,0,0.06)",
            }}
          >
            {editingId === a.id ? (
              <div
                style={{
                  display: "flex",
                  flexDirection: "column",
                  gap: "0.5rem",
                }}
              >
                <input
                  value={editFields.name}
                  onChange={e =>
                    setEditFields(f => ({
                      ...f,
                      name: e.target.value,
                    }))
                  }
                />
                <input
                  value={editFields.type}
                  onChange={e =>
                    setEditFields(f => ({
                      ...f,
                      type: e.target.value,
                    }))
                  }
                />
                <input
                  value={editFields.owner}
                  onChange={e =>
                    setEditFields(f => ({
                      ...f,
                      owner: e.target.value,
                    }))
                  }
                />
                <div style={{ display: "flex", gap: "0.5rem" }}>
                  <button
                    onClick={() => saveEdit(a.id)}
                    style={{
                      background: "#36B37E",
                      color: "#fff",
                      border: "none",
                      borderRadius: "4px",
                      padding: "0.25rem 0.5rem",
                    }}
                  >
                    Save
                  </button>
                  <button
                    onClick={cancelEdit}
                    style={{
                      background: "#DE350B",
                      color: "#fff",
                      border: "none",
                      borderRadius: "4px",
                      padding: "0.25rem 0.5rem",
                    }}
                  >
                    Cancel
                  </button>
                </div>
              </div>
            ) : (
              <div onClick={() => startEditing(a)} style={{ cursor: "pointer" }}>
                <div style={{ fontWeight: "bold" }}>{a.name}</div>
                <div style={{ fontSize: "0.85rem", color: "#555" }}>
                  {a.type} | {a.owner || "-"}
                </div>
              </div>
            )}

            {/* Hover Toolbar */}
            {hoveredId === a.id && editingId !== a.id && (
              <div
                style={{
                  position: "absolute",
                  top: "4px",
                  right: "4px",
                  display: "flex",
                  gap: "0.5rem",
                }}
              >
                <button
                  onClick={() => startEditing(a)}
                  style={{
                    background: "transparent",
                    border: "none",
                    color: "#0052CC",
                    cursor: "pointer",
                    fontWeight: "bold",
                  }}
                >
                  Edit
                </button>
                <button
                  onClick={() => deleteAsset(a.id)}
                  style={{
                    background: "transparent",
                    border: "none",
                    color: "#DE350B",
                    cursor: "pointer",
                    fontWeight: "bold",
                  }}
                >
                  Delete
                </button>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}
