import React, { useState } from 'react';

export default function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const styles = {
    container: {
      minHeight: "100vh",
      backgroundColor: "#0f0f1a",
      background: `
        linear-gradient(135deg, #0f0f1a 0%, #1a1a2e 50%, #16213e 100%)
      `,
      display: "flex",
      alignItems: "center",
      justifyContent: "center",
      fontFamily: "'Inter', -apple-system, BlinkMacSystemFont, sans-serif",
      padding: "2rem"
    },
    card: {
      background: "rgba(15, 15, 25, 0.8)",
      backdropFilter: "blur(20px)",
      borderRadius: "20px",
      padding: "3rem",
      border: "1px solid rgba(255, 255, 255, 0.1)",
      boxShadow: `
        0 25px 50px -12px rgba(0, 0, 0, 0.5),
        inset 0 1px 0 rgba(255, 255, 255, 0.1)
      `,
      width: "100%",
      maxWidth: "420px",
      position: "relative",
      overflow: "hidden"
    },
    cardGlow: {
      position: "absolute",
      top: 0,
      left: 0,
      right: 0,
      height: "2px",
      background: "linear-gradient(90deg, #667eea, #764ba2)",
      borderRadius: "20px 20px 0 0"
    },
    logo: {
      textAlign: "center",
      marginBottom: "2rem"
    },
    logoText: {
      fontSize: "2.5rem",
      fontWeight: "700",
      background: "linear-gradient(135deg, #667eea, #764ba2)",
      backgroundClip: "text",
      WebkitBackgroundClip: "text",
      WebkitTextFillColor: "transparent",
      margin: "0"
    },
    tagline: {
      textAlign: "center",
      color: "#94a3b8",
      fontSize: "1.1rem",
      fontWeight: "400",
      margin: "0.5rem 0 2rem 0"
    },
    welcomeText: {
      fontSize: "1.5rem",
      fontWeight: "600",
      color: "#f1f5f9",
      margin: "0 0 2rem 0",
      textAlign: "center"
    },
    formGroup: {
      marginBottom: "1.5rem"
    },
    label: {
      display: "block",
      fontSize: "0.9rem",
      fontWeight: "500",
      color: "#cbd5e1",
      marginBottom: "0.5rem"
    },
    input: {
      width: "100%",
      padding: "0.875rem 1rem",
      background: "rgba(255, 255, 255, 0.05)",
      border: "1px solid rgba(255, 255, 255, 0.1)",
      borderRadius: "10px",
      fontSize: "1rem",
      color: "#f1f5f9",
      transition: "all 0.3s ease",
      boxSizing: "border-box"
    },
    inputFocus: {
      outline: "none",
      borderColor: "rgba(102, 126, 234, 0.5)",
      boxShadow: "0 0 0 3px rgba(102, 126, 234, 0.1)"
    },
    separator: {
      height: "1px",
      background: "linear-gradient(90deg, transparent, rgba(255,255,255,0.1), transparent)",
      margin: "2rem 0"
    },
    signInButton: {
      width: "100%",
      padding: "0.875rem 1rem",
      background: "linear-gradient(135deg, #667eea, #764ba2)",
      border: "none",
      borderRadius: "10px",
      fontSize: "1rem",
      fontWeight: "600",
      color: "#ffffff",
      cursor: "pointer",
      transition: "all 0.3s ease",
      marginBottom: "1.5rem"
    },
    signInButtonHover: {
      transform: "translateY(-1px)",
      boxShadow: "0 10px 20px rgba(102, 126, 234, 0.3)"
    },
    footerText: {
      textAlign: "center",
      color: "#64748b",
      fontSize: "0.9rem",
      margin: "0"
    },
    getStartedLink: {
      color: "#667eea",
      textDecoration: "none",
      fontWeight: "500"
    },
    copyright: {
      textAlign: "center",
      color: "#475569",
      fontSize: "0.8rem",
      margin: "2rem 0 0 0"
    }
  };

  return (
    <div style={styles.container}>
      <div style={styles.card}>
        <div style={styles.cardGlow} />

        <div style={styles.logo}>
          <h1 style={styles.logoText}>Flow Track</h1>
          <p style={styles.tagline}>Modern Project Management Solution</p>
        </div>

        <h2 style={styles.welcomeText}>Welcome User</h2>

        <form>
          <div style={styles.formGroup}>
            <label style={styles.label}>Email Address</label>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="Enter your email"
              style={styles.input}
              onFocus={(e) => e.target.style = { ...styles.input, ...styles.inputFocus }}
              onBlur={(e) => e.target.style = styles.input}
            />
          </div>

          <div style={styles.formGroup}>
            <label style={styles.label}>Password</label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Enter your password"
              style={styles.input}
              onFocus={(e) => e.target.style = { ...styles.input, ...styles.inputFocus }}
              onBlur={(e) => e.target.style = styles.input}
            />
          </div>

          <div style={styles.separator} />

          <button
            type="submit"
            style={styles.signInButton}
            onMouseEnter={(e) => e.target.style = { ...styles.signInButton, ...styles.signInButtonHover }}
            onMouseLeave={(e) => e.target.style = styles.signInButton}
          >
            Sign In
          </button>
        </form>

        <p style={styles.footerText}>
          New to Flow Track?{" "}
          <a href="#" style={styles.getStartedLink}>Get started</a>
        </p>

        <p style={styles.copyright}>
          ¬© 2025 Vunnith Technologies Pvt Ltd
        </p>
      </div>

      <style>
        {`
          @import url('https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;600;700&display=swap');

          * {
            box-sizing: border-box;
          }

          body {
            margin: 0;
            background: #0f0f1a;
          }

          input::placeholder {
            color: #64748b;
          }

          a:hover {
            text-decoration: underline;
          }
        `}
      </style>
    </div>
  );
}
