          display: flex;
          justify-content: flex-end;
          gap: 12px;
          margin-bottom: 10px;
        }

        .create-epic-btn {
          background: #1976d2;
          color: white;
          border: none;
          padding: 10px 16px;
          border-radius: 6px;
          cursor: pointer;
          font-size: 14px;
          font-weight: 600;
          transition: background-color 0.2s;
          margin: 5px;
        }

        .create-epic-btn:hover {
          background: #1565c0;
        }

