import React, { useEffect, useState } from "react";
import { listUsers, addUser, updateUser, deleteUser } from "../../services/mockApi";
import { v4 as uuidv4 } from "uuid";

const defaultRoles = [
  "Associate Developer",
  "Senior Associate Developer",
  "HR",
  "Administration"
];
const defaultDepartments = [
  "Frontend",
  "Backend",
  "Marketing",
  "AI/ML",
  "DevOps",
  "Testing",
  "FlowTrack",
  "NetWork",
  "Hr"
];

export default function Users() {
  const defaultNewUser = {
    firstName: "",
    email: "",
    role: defaultRoles[0],
    department: defaultDepartments[0],
    active: true,
    mobileNumber: "",
    passwordResetNeeded: false,
    profileFile: null,
  };

  const [users, setUsers] = useState([]);
  const [editingId, setEditingId] = useState(null);
  const [editForm, setEditForm] = useState({});
  const [error, setError] = useState(null);
  const [successMessage, setSuccessMessage] = useState(null);
  const [roles] = useState(defaultRoles);
  const [departments] = useState(defaultDepartments);

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const data = await listUsers();
        setUsers(data);
      } catch (err) {
        setError("Failed to load users");
      }
    };
    fetchUsers();
  }, []);

  const startEditing = (user) => {
    setEditingId(user.id);
    setEditForm({
      firstName: user.firstName || "",
      email: user.email || "",
      role: user.role || defaultRoles[0],
      department: user.department || defaultDepartments[0],
      mobileNumber: user.mobileNumber || "",
      passwordResetNeeded: user.passwordResetNeeded || false,
    });
    setError(null);
    setSuccessMessage(null);
  };

  const handleEditChange = (e) => {
    const { name, value, type, checked } = e.target;
    setEditForm((prev) => ({
      ...prev,
      [name]: type === "checkbox" ? checked : value,
    }));
    setError(null);
    setSuccessMessage(null);
  };

  const saveEdit = async () => {
    if (!editForm.firstName.trim() || !editForm.email.trim()) {
      setError("First Name and Email are required.");
      setSuccessMessage(null);
      return;
    }
    try {
      const updatedUser = {
        ...editForm,
        id: editingId,
        name: editForm.firstName.trim(),
      };
      await updateUser(updatedUser);
      const updatedUsers = await listUsers();
      setUsers(updatedUsers);
      setEditingId(null);
      setEditForm({});
      setError(null);
      setSuccessMessage("User updated successfully!");
    } catch (err) {
      setError("Failed to update user");
      setSuccessMessage(null);
    }
  };

  return (
    <div>
      <h2>Users List</h2>
      {users.map(user => (
        <div key={user.id} style={{ marginBottom: 16 }}>
          {editingId === user.id ? (
            <>
              <input
                name="firstName"
                value={editForm.firstName}
                onChange={handleEditChange}
                placeholder="Full Name"
              />
              <input
                name="email"
                type="email"
                value={editForm.email}
                onChange={handleEditChange}
                placeholder="Email"
              />
              <select
                name="role"
                value={editForm.role}
                onChange={handleEditChange}
              >
                {roles.map(r => (
                  <option key={r} value={r}>{r}</option>
                ))}
              </select>
              <select
                name="department"
                value={editForm.department}
                onChange={handleEditChange}
              >
                {departments.map(d => (
                  <option key={d} value={d}>{d}</option>
                ))}
              </select>
              <input
                name="mobileNumber"
                type="tel"
                value={editForm.mobileNumber}
                onChange={handleEditChange}
                placeholder="Mobile Number"
              />
              <label>
                <input
                  type="checkbox"
                  name="passwordResetNeeded"
                  checked={editForm.passwordResetNeeded}
                  onChange={handleEditChange}
                />
                Password Reset Needed
              </label>
              <button onClick={saveEdit}>Save</button>
            </>
          ) : (
            <>
              <span>{user.firstName}</span>{" | "}
              <span>{user.email}</span>{" | "}
              <span>{user.role}</span>{" | "}
              <span>{user.department}</span>{" | "}
              <span>{user.mobileNumber || "-"}</span>
              <button onClick={() => startEditing(user)}>Edit</button>
            </>
          )}
        </div>
      ))}
      {users.length === 0 && <p>No users to display.</p>}
      {error && <p style={{ color: "red" }}>{error}</p>}
      {successMessage && <p style={{ color: "green" }}>{successMessage}</p>}
    </div>
  );
}
