import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

const STATUSES = [
  'backlog', 'todo', 'analysis', 'inprogress', 'blocked', 'code review', 'qa', 'milestone', 'done'
];

// Mock issues with two epics swimlanes for demo
const mockIssues = [
  { id: 'i1', epic: 'p1', epicName: 'Frontend', status: 'todo', type: 'Task', title: 'Setup repo', assignee: 'u2', storyPoints: 3, labels: [], dueDate: '' },
  { id: 'i2', epic: 'p1', epicName: 'Frontend', status: 'inprogress', type: 'Bug', title: 'Login bug', assignee: 'u2', storyPoints: 1, labels: [], dueDate: '' },
  { id: 'i3', epic: 'p2', epicName: 'Middleware', status: 'todo', type: 'Subtask', title: 'API integration', assignee: 'u4', storyPoints: 2, labels: [], dueDate: '' }
];

const simulateApiDelay = () => new Promise(resolve => setTimeout(resolve, 200));

const listIssues = async (projectId) => {
  await simulateApiDelay();
  return mockIssues.filter(i => !projectId || i.epic === projectId || i.projectId === projectId);
};

const moveIssue = async (issueId, status) => {
  await simulateApiDelay();
  const issue = mockIssues.find(i => i.id === issueId);
  if (!issue) throw new Error('Issue not found');
  issue.status = status;
};

const createIssueAPI = async (issue) => {
  await simulateApiDelay();
  mockIssues.push(issue);
};

const getSwimlanes = issues =>
  [...new Set(issues.map(i => i.epic || i.projectId))].map(id => {
    const epicIssues = issues.filter(i => (i.epic || i.projectId) === id);
    const epicTitle = epicIssues.find(i => i.epicName)?.epicName || id;
    return {
      id,
      title: epicTitle,
      issues: epicIssues
    };
  });

export default function KanbanBoard() {
  const { projectId } = useParams();
  const [issues, setIssues] = useState([]);
  const [openSwimlanes, setOpenSwimlanes] = useState({});
  const [customTitles, setCustomTitles] = useState({});
  const [createLaneId, setCreateLaneId] = useState(null);
  const [newTaskText, setNewTaskText] = useState('');
  const [newTaskType, setNewTaskType] = useState('Task');

  useEffect(() => {
    const fetchIssues = async () => {
      try {
        const data = await listIssues(projectId);
        setIssues(data);
        const lanes = getSwimlanes(data);
        const initialOpen = {};
        const initialTitles = {};
        lanes.forEach(lane => {
          initialOpen[lane.id] = true;
          initialTitles[lane.id] = lane.title;
        });
        setOpenSwimlanes(initialOpen);
        setCustomTitles(initialTitles);
      } catch (err) {
        console.error('Failed to load issues:', err);
      }
    };
    fetchIssues();
  }, [projectId]);

  const toggleSwimlane = (id) => {
    setOpenSwimlanes(prev => ({ ...prev, [id]: !prev[id] }));
  };

  const onTitleChange = (id, value) => {
    setCustomTitles(prev => ({ ...prev, [id]: value }));
  };

  const byStatus = (collection, status) => collection.filter(i => i.status === status);

  const onDragStart = (e, issueId) => {
    e.dataTransfer.setData('text/plain', issueId);
  };

  const onDrop = async (e, targetStatus, swimlaneId) => {
    e.preventDefault();
    try {
      const issueId = e.dataTransfer.getData('text/plain');
      const issue = issues.find(i => i.id === issueId);
      const srcLaneId = issue.epic || issue.projectId;
      if (srcLaneId === swimlaneId && issue.status !== targetStatus) {
        await moveIssue(issueId, targetStatus);
        const refreshed = await listIssues(projectId);
        setIssues(refreshed);
      }
    } catch (err) {
      console.error('Error moving issue:', err);
    }
  };

  const onDragOver = e => e.preventDefault();

  const handleCreateClick = laneId => {
    setCreateLaneId(laneId);
    setNewTaskText('');
    setNewTaskType('Task');
  };

  const handleCreateSubmit = async lane => {
    if (!newTaskText.trim()) {
      alert('Task title is required');
      return;
    }
    const newIssue = {
      id: Math.random().toString(36).slice(2),
      epic: lane.id,
      projectId: lane.id,
      epicName: lane.title,
      title: newTaskText,
      status: 'backlog',
      assignee: '',
      type: newTaskType,
      storyPoints: '',
      labels: [],
      dueDate: ''
    };
    try {
      await createIssueAPI(newIssue);
      const refreshed = await listIssues(projectId);
      setIssues(refreshed);
      setCreateLaneId(null);
      setNewTaskText('');
      setNewTaskType('Task');
    } catch (err) {
      console.error('Error creating issue:', err);
    }
  };

  const handleCreateCancel = () => {
    setCreateLaneId(null);
    setNewTaskText('');
    setNewTaskType('Task');
  };

  return (
    <div className="board-wrap">
      {getSwimlanes(issues).map(lane => {
        const isOpen = openSwimlanes[lane.id];
        return (
          <section className="swimlane" key={lane.id}>
            <header className="swimlane-header">
              <button
                className="swimlane-toggle"
                aria-label={isOpen ? `Collapse swimlane` : `Expand swimlane`}
                aria-expanded={isOpen}
                onClick={() => toggleSwimlane(lane.id)}
              >
                {isOpen ? 'â–¼' : 'â–¶'}
              </button>
              <span className="swimlane-icon" aria-hidden="true" style={{ marginRight: 6 }}>âš¡</span>
              <input
                className="swimlane-title-input"
                value={customTitles[lane.id] || ''}
                onChange={e => onTitleChange(lane.id, e.target.value)}
                aria-label={`Edit epic title for swimlane ${lane.id}`}
                spellCheck="false"
              />
              <span className="swimlane-count">{lane.issues.length} work items</span>
            </header>
            {isOpen && (
              <div className="kanban-row" id={`swimlane-content-${lane.id}`}>
                {STATUSES.map(status => {
                  const issuesForStatus = byStatus(lane.issues, status);
                  return (
                    <div
                      className="kanban-column"
                      key={status}
                      onDragOver={onDragOver}
                      onDrop={e => onDrop(e, status, lane.id)}
                    >
                      <div className="col-header">
                        <span>{status.replace(/_/g, ' ').toUpperCase()}</span>
                        {issuesForStatus.length > 0 && <span className="col-count">{issuesForStatus.length}</span>}
                      </div>
                      <div className="col-create">
                        {status === 'backlog' && (
                          createLaneId === lane.id ? (
                            <div className="create-card">
                              <textarea
                                rows={2}
                                className="create-input"
                                placeholder="What needs to be done?"
                                value={newTaskText}
                                onChange={e => setNewTaskText(e.target.value)}
                              />
                              <div className="create-actions">
                                <select
                                  value={newTaskType}
                                  onChange={e => setNewTaskType(e.target.value)}
                                  className="create-select"
                                >
                                  <option value="Task">Task</option>
                                  <option value="Subtask">Subtask</option>
                                  <option value="Bug">Bug</option>
                                </select>
                                <button className="create-btn" onClick={() => handleCreateSubmit(lane)}>âœ”</button>
                                <button className="create-btn" onClick={handleCreateCancel}>âœ–</button>
                              </div>
                            </div>
                          ) : (
                            <span onClick={() => handleCreateClick(lane.id)} style={{ cursor: 'pointer' }}>+ Create</span>
                          )
                        )}
                      </div>
                      {issuesForStatus.map(issue => (
                        <div
                          className="card-item"
                          tabIndex={0}
                          key={issue.id}
                          draggable
                          onDragStart={e => onDragStart(e, issue.id)}
                        >
                          <div className="card-top">
                            <span className="card-tag">{issue.type}</span>
