import React, { useEffect, useState } from 'react';
import { listUsers, addUser, updateUser, deleteUser } from '../../services/mockApi';
import { v4 as uuidv4 } from 'uuid';
import {
  FiUserPlus, FiEdit2, FiTrash2, FiCheck, FiX, FiSearch, FiUpload,
  FiPlus, FiMinus
} from 'react-icons/fi';

const ROLES_KEY = 'user-management-roles';
const DEPARTMENTS_KEY = 'user-management-departments';

const defaultRoles = [
  'Associate Developer',
  'Senior Associate Developer',
  'HR',
  'Administration'
];
const defaultDepartments = [
  'Frontend', 'Backend', 'Middleware', 'AI/ML','DevOps','Testing','FlowTrack','NetWork','Hr'
];

function getStored(key, fallback) {
  const saved = localStorage.getItem(key);
  try {
    return saved ? JSON.parse(saved) : fallback;
  } catch {
    return fallback;
  }
}
function setStored(key, value) {
  localStorage.setItem(key, JSON.stringify(value));
}

export default function Users() {
  const defaultNewUser = {
    firstName: '',
    lastName: '',
    email: '',
    role: defaultRoles[0],
    department: defaultDepartments[0],
    active: true,
    mobileNumber: '',
    passwordResetNeeded: false,
    profileFile: null,
  };

  const [users, setUsers] = useState([]);
  const [newUserForm, setNewUserForm] = useState(defaultNewUser);
  const [editingId, setEditingId] = useState(null);
  const [editForm, setEditForm] = useState({});
  const [searchTerm, setSearchTerm] = useState('');
  const [error, setError] = useState(null);

  const [roles, setRoles] = useState(() => getStored(ROLES_KEY, defaultRoles));
  const [departments, setDepartments] = useState(() => getStored(DEPARTMENTS_KEY, defaultDepartments));

  const [showRoleModal, setShowRoleModal] = useState(false);
  const [showDepartmentModal, setShowDepartmentModal] = useState(false);
  const [roleInput, setRoleInput] = useState('');
  const [departmentInput, setDepartmentInput] = useState('');
  const [roleToDelete, setRoleToDelete] = useState('');
  const [departmentToDelete, setDepartmentToDelete] = useState('');

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const data = await listUsers();
        setUsers(data);
      } catch (err) {
        console.error('Failed to load users:', err);
        setError('Failed to load users');
      }
    };
    fetchUsers();
  }, []);

  useEffect(() => { setStored(ROLES_KEY, roles); }, [roles]);
  useEffect(() => { setStored(DEPARTMENTS_KEY, departments); }, [departments]);

  const handleNewUserChange = (e) => {
    const { name, value, type, checked, files } = e.target;
    setNewUserForm(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : (type === 'file' ? files[0] : value),
    }));
  };

  const handleEditChange = (e) => {
    const { name, value, type, checked, files } = e.target;
    setEditForm(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : (type === 'file' ? files[0] : value),
    }));
  };

  const create = async () => {
    if (!newUserForm.firstName.trim() || !newUserForm.lastName.trim() || !newUserForm.email.trim()) {
      setError('First Name, Last Name, and Email are required.');
      return;
    }
    try {
      const newUser = {
        id: uuidv4(),
        ...newUserForm,
        name: `${newUserForm.firstName.trim()} ${newUserForm.lastName.trim()}`,
        profileFile: newUserForm.profileFile
          ? { name: newUserForm.profileFile.name, size: newUserForm.profileFile.size }
          : null
      };
      await addUser(newUser);
      const updatedUsers = await listUsers();
      setUsers(updatedUsers);
      setNewUserForm(defaultNewUser);
      setError(null);
    } catch (err) {
      console.error('Failed to add user:', err);
      setError('Failed to add user');
    }
  };

  const startEdit = (u) => {
    setEditingId(u.id);
    setEditForm({ ...u });
    setError(null);
  };

  const cancelEdit = () => {
    setEditingId(null);
    setEditForm({});
    setError(null);
  };

  const saveEdit = async (id) => {
    if (!editForm.firstName.trim() || !editForm.lastName.trim() || !editForm.email.trim()) {
      setError('First Name, Last Name, and Email cannot be empty.');
      return;
    }
    try {
      const updatedUser = {
        ...editForm,
        name: `${editForm.firstName.trim()} ${editForm.lastName.trim()}`,
        profileFile: editForm.profileFile && editForm.profileFile.name
          ? { name: editForm.profileFile.name, size: editForm.profileFile.size }
          : editForm.profileFile
      };
      await updateUser(updatedUser);
      const updatedUsers = await listUsers();
      setUsers(updatedUsers);
      cancelEdit();
      setError(null);
    } catch (err) {
      console.error('Failed to update user:', err);
      setError('User not found or failed to update');
    }
  };

  const remove = async (id) => {
    try {
      await deleteUser(id);
      const updatedUsers = await listUsers();
      setUsers(updatedUsers);
      setError(null);
    } catch (err) {
      console.error('Failed to delete user:', err);
      setError('User not found or failed to delete');
    }
  };

  const filteredUsers = users.filter(user => {
    const searchLower = searchTerm.toLowerCase();
    return (
      (user.name && user.name.toLowerCase().includes(searchLower)) ||
      (user.firstName && user.firstName.toLowerCase().includes(searchLower)) ||
      (user.lastName && user.lastName.toLowerCase().includes(searchLower)) ||
      (user.email && user.email.toLowerCase().includes(searchLower)) ||
      (user.role && user.role.toLowerCase().includes(searchLower)) ||
      (user.department && user.department.toLowerCase().includes(searchLower))
    );
  });

  const getRoleColor = (roleOrDepartment) => {
    const colors = {
      'admin': 'role-admin',
      'manager': 'role-manager',
      'developer': 'role-developer',
      'reporter': 'role-reporter',
      'engineering': 'role-developer',
      'sales': 'role-sales',
      'hr': 'role-hr',
      'marketing': 'role-marketing',
      'associate developer': 'role-developer',
      'senior associate developer': 'role-developer',
      'administration': 'role-admin'
    };
    return colors[roleOrDepartment.toLowerCase()] || '';
  };

  // Modal handlers omitted for brevity (Same as you provided)

  return (
    <div className="users-container">
      <div className="users-header">
        <h1 className="users-title">User Management</h1>
        <p className="users-subtitle">Manage your users, roles, and departments</p>
      </div>

      <div className="users-grid">
        {/* User creation form */}
        <div className="users-form-card glassmorphism">
          <div className="card-header">
            <h2 className="card-title"><FiUserPlus className="icon-spacing" />Add New User</h2>
          </div>

          <div className="form-grid-expanded">
            <div className="input-group">
              <label className="input-label" htmlFor="firstName">First Name</label>
              <input
                className="modern-input"
                id="firstName"
                name="firstName"
                type="text"
                value={newUserForm.firstName}
                onChange={handleNewUserChange}
              />
            </div>
            <div className="input-group">
              <label className="input-label" htmlFor="lastName">Last Name</label>
              <input
                className="modern-input"
                id="lastName"
                name="lastName"
                type="text"
                value={newUserForm.lastName}
                onChange={handleNewUserChange}
              />
            </div>

            <div className="input-group">
              <label className="input-label" htmlFor="email">Email</label>
              <input
                className="modern-input"
                id="email"
                name="email"
                type="email"
                value={newUserForm.email}
                onChange={handleNewUserChange}
              />
            </div>

            <div className="input-group">
              <label className="input-label" htmlFor="role">Role</label>
              <select
                className="modern-select"
                id="role"
                name="role"
                value={newUserForm.role}
                onChange={handleNewUserChange}
              >
                {roles.map(r => <option key={r} value={r}>{r}</option>)}
              </select>
            </div>

            <div className="input-group">
              <label className="input-label" htmlFor="department">Department</label>
              <select
                className="modern-select"
                id="department"
                name="department"
                value={newUserForm.department}
                onChange={handleNewUserChange}
              >
                {departments.map(d => <option key={d} value={d}>{d}</option>)}
              </select>
            </div>

            <div className="input-group">
              <label className="input-label" htmlFor="mobileNumber">Mobile Number</label>
              <input
                className="modern-input"
                id="mobileNumber"
                name="mobileNumber"
                type="tel"
                value={newUserForm.mobileNumber}
                onChange={handleNewUserChange}
              />
            </div>

            <div className="input-group checkbox-group">
              <label className="checkbox-label" htmlFor="active">
                <input
                  id="active"
                  name="active"
                  type="checkbox"
                  checked={newUserForm.active}
                  onChange={handleNewUserChange}
                />
                Active
              </label>
            </div>

            <div className="input-group checkbox-group">
              <label className="checkbox-label" htmlFor="passwordResetNeeded">
                <input
                  id="passwordResetNeeded"
                  name="passwordResetNeeded"
                  type="checkbox"
                  checked={newUserForm.passwordResetNeeded}
                  onChange={handleNewUserChange}
                />
                Password Reset Needed
              </label>
            </div>

            <div className="input-group file-group">
              <label className="file-upload-btn" htmlFor="profileFile">
                <FiUpload className="btn-icon" />{newUserForm.profileFile ? newUserForm.profileFile.name : "Upload Profile File"}
              </label>
              <input
                id="profileFile"
                name="profileFile"
                type="file"
                onChange={handleNewUserChange}
                accept="image/*"
              />
            </div>
          </div>

          {error && <p className="error-message">{error}</p>}

          <button className="create-btn" onClick={create}>
            <FiPlus className="btn-icon" /> Create User
          </button>
        </div>

        {/* User list with search */}
        <div className="users-list-card glassmorphism">
          <div className="card-header">
            <h2 className="card-title">Users</h2>
            <div className="search-container">
              <input
                type="text"
                className="search-input"
                placeholder="Search users..."
                value={searchTerm}
                onChange={e => setSearchTerm(e.target.value)}
              />
              <FiSearch className="search-icon" />
            </div>
          </div>

          <div className="table-container">
            <table className="modern-table">
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Email</th>
                  <th>Role</th>
                  <th>Department</th>
                  <th>Active</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {filteredUsers.length === 0 && (
                  <tr>
                    <td colSpan="6" className="empty-state">No users found.</td>
                  </tr>
                )}

                {filteredUsers.map(user => {
                  const isEditing = editingId === user.id;
                  return (
                    <tr key={user.id} className={isEditing ? 'editing-row' : ''}>
                      {/* Name */}
                      <td>
                        {isEditing ? (
                          <>
                            <input
                              className="edit-input"
                              type="text"
                              name="firstName"
                              placeholder="First Name"
                              value={editForm.firstName || ''}
                              onChange={handleEditChange}
                            />
                            <input
                              className="edit-input"
                              type="text"
                              name="lastName"
                              placeholder="Last Name"
                              value={editForm.lastName || ''}
                              onChange={handleEditChange}
                              style={{ marginTop: '4px' }}
                            />
                          </>
                        ) : (
                          user.name
                        )}
                      </td>

                      {/* Email */}
                      <td>
                        {isEditing ? (
                          <input
                            className="edit-input"
                            type="email"
                            name="email"
                            value={editForm.email || ''}
                            onChange={handleEditChange}
                          />
                        ) : (
                          user.email
                        )}
                      </td>

                      {/* Role */}
                      <td>
                        {isEditing ? (
                          <select
                            className="edit-select"
                            name="role"
                            value={editForm.role || roles[0]}
                            onChange={handleEditChange}
                          >
                            {roles.map(role => <option key={role} value={role}>{role}</option>)}
                          </select>
                        ) : (
                          <span className={`role-badge ${getRoleColor(user.role)}`}>{user.role}</span>
                        )}
                      </td>

                      {/* Department */}
                      <td>
                        {isEditing ? (
                          <select
                            className="edit-select"
                            name="department"
                            value={editForm.department || departments[0]}
                            onChange={handleEditChange}
                          >
                            {departments.map(dep => <option key={dep} value={dep}>{dep}</option>)}
                          </select>
                        ) : (
                          user.department
                        )}
                      </td>

                      {/* Active */}
                      <td>
                        {isEditing ? (
                          <input
                            type="checkbox"
                            name="active"
                            checked={!!editForm.active}
                            onChange={handleEditChange}
                          />
                        ) : (
                          <span className={`status-dot ${user.active ? 'status-active' : 'status-inactive'}`} title={user.active ? 'Active' : 'Inactive'} />
                        )}
                      </td>

                      {/* Actions */}
                      <td className="action-buttons">
                        {isEditing ? (
                          <>
                            <button className="btn-success" onClick={() => saveEdit(user.id)} title="Save">
                              <FiCheck />
                            </button>
                            <button className="btn-cancel" onClick={cancelEdit} title="Cancel">
                              <FiX />
                            </button>
                          </>
                        ) : (
                          <>
                            <button className="btn-edit" onClick={() => startEdit(user)} title="Edit">
                              <FiEdit2 />
                            </button>
                            <button className="btn-delete" onClick={() => remove(user.id)} title="Delete">
                              <FiTrash2 />
                            </button>
                          </>
                        )}
                      </td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          </div>

        </div>
      </div>

      {/* Role and Department modals can be implemented below similarly as per your original code */}
      {/* For brevity I omitted modal JSX - you can add it back based on your needs */}
      
      <style jsx>{`
        /* Your styles from the original code included here */
        /* For brevity, omitted here but in your actual project include your whole CSS */
      `}</style>
    </div>
  );
}
