// src/components/KanbanBoard.js
import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

/* --------- Shared Data --------- */
export let mockEpics = [
  { id: "e1", name: "Frontend", projectId: "1" },
  { id: "e2", name: "Backend", projectId: "2" }
];

export let mockIssues = [
  { id: "i1", title: "Login Page", status: "todo", epic: "e1", projectId: "1" },
  { id: "i2", title: "API Setup", status: "inprogress", epic: "e2", projectId: "2" }
];

/* --------- API helpers --------- */
export const deleteEpicsByProject = (projectId) => {
  mockEpics = mockEpics.filter((e) => e.projectId !== projectId);
};
export const deleteIssuesByProject = (projectId) => {
  mockIssues = mockIssues.filter((i) => i.projectId !== projectId);
};

const STATUSES = ["backlog", "todo", "inprogress", "blocked", "done"];

const simulateApiDelay = (time = 200) =>
  new Promise((resolve) => setTimeout(resolve, time));

const listEpics = async (projectId) => {
  await simulateApiDelay();
  return mockEpics.filter((e) => e.projectId === projectId);
};

const listIssues = async (projectId) => {
  await simulateApiDelay();
  return mockIssues.filter((i) => i.projectId === projectId);
};

const createEpicAPI = async (epicName, projectId) => {
  await simulateApiDelay();
  const newEpic = {
    id: "e" + (mockEpics.length + 1),
    name: epicName,
    projectId
  };
  mockEpics.push(newEpic);
  return newEpic;
};

const deleteEpicAPI = async (epicId) => {
  await simulateApiDelay();
  mockEpics = mockEpics.filter((e) => e.id !== epicId);
  mockIssues = mockIssues.filter((i) => i.epic !== epicId);
};

const createIssueAPI = async (title, status, epicId, projectId) => {
  await simulateApiDelay();
  const newIssue = {
    id: "i" + (mockIssues.length + 1),
    title,
    status,
    epic: epicId,
    projectId
  };
  mockIssues.push(newIssue);
  return newIssue;
};

const deleteIssueAPI = async (issueId) => {
  await simulateApiDelay();
  mockIssues = mockIssues.filter((i) => i.id !== issueId);
};

/* --------- Component --------- */
export default function KanbanBoard() {
  const { projectId } = useParams();
  const [epics, setEpics] = useState([]);
  const [issues, setIssues] = useState([]);
  const [newEpicName, setNewEpicName] = useState("");
  const [newIssueTitle, setNewIssueTitle] = useState("");
  const [selectedEpic, setSelectedEpic] = useState("");
  const [selectedStatus, setSelectedStatus] = useState("todo");

  useEffect(() => {
    const fetchData = async () => {
      setEpics(await listEpics(projectId));
      setIssues(await listIssues(projectId));
    };
    fetchData();
  }, [projectId]);

  const handleCreateEpic = async () => {
    if (!newEpicName.trim()) return;
    await createEpicAPI(newEpicName, projectId);
    setEpics(await listEpics(projectId));
    setNewEpicName("");
  };

  const handleDeleteEpic = async (epicId) => {
    await deleteEpicAPI(epicId);
    setEpics(await listEpics(projectId));
    setIssues(await listIssues(projectId));
  };

  const handleCreateIssue = async () => {
    if (!newIssueTitle.trim() || !selectedEpic) return;
    await createIssueAPI(newIssueTitle, selectedStatus, selectedEpic, projectId);
    setIssues(await listIssues(projectId));
    setNewIssueTitle("");
  };

  const handleDeleteIssue = async (issueId) => {
    await deleteIssueAPI(issueId);
    setIssues(await listIssues(projectId));
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>Kanban Board - Project {projectId}</h2>

      {/* Create Epic */}
      <div style={{ marginBottom: "20px" }}>
        <input
          type="text"
          placeholder="New Epic Name"
          value={newEpicName}
          onChange={(e) => setNewEpicName(e.target.value)}
        />
        <button onClick={handleCreateEpic}>Add Epic</button>
      </div>

      {/* Create Issue */}
      <div style={{ marginBottom: "20px" }}>
        <input
          type="text"
          placeholder="New Issue Title"
          value={newIssueTitle}
          onChange={(e) => setNewIssueTitle(e.target.value)}
        />
        <select
          value={selectedEpic}
          onChange={(e) => setSelectedEpic(e.target.value)}
        >
          <option value="">Select Epic</option>
          {epics.map((epic) => (
            <option key={epic.id} value={epic.id}>
              {epic.name}
            </option>
          ))}
        </select>
        <select
          value={selectedStatus}
          onChange={(e) => setSelectedStatus(e.target.value)}
        >
          {STATUSES.map((s) => (
            <option key={s}>{s}</option>
          ))}
        </select>
        <button onClick={handleCreateIssue}>Add Issue</button>
      </div>

      {/* Columns */}
      <div style={{ display: "flex", gap: "20px" }}>
        {STATUSES.map((status) => (
          <div key={status} style={{ flex: 1, background: "#f5f5f5", padding: "10px" }}>
            <h3>{status.toUpperCase()}</h3>
            {epics.map((epic) => (
              <div key={epic.id} style={{ marginBottom: "10px" }}>
                <div>
                  <strong>{epic.name}</strong>
                  <button
                    onClick={() => handleDeleteEpic(epic.id)}
                    style={{ marginLeft: "10px", color: "red" }}
                  >
                    X
                  </button>
                </div>
                {issues
                  .filter((i) => i.status === status && i.epic === epic.id)
                  .map((i) => (
                    <div key={i.id} style={{ background: "#ddd", margin: "4px 0", padding: "4px" }}>
                      {i.title}
                      <button
                        onClick={() => handleDeleteIssue(i.id)}
                        style={{ marginLeft: "10px", color: "red" }}
                      >
                        X
                      </button>
                    </div>
                  ))}
              </div>
            ))}
          </div>
        ))}
      </div>
    </div>
  );
}
