import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom"; // ✅ Import navigation hook

function LoginPage() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [isHovered, setIsHovered] = useState(false);
  const [activeInput, setActiveInput] = useState(null);
  const [showPassword, setShowPassword] = useState(false);
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });

  // Floating animation elements
  const [particles, setParticles] = useState([]);
  const [orbs, setOrbs] = useState([]);

  const navigate = useNavigate(); // ✅ React Router hook

  useEffect(() => {
    // Create floating particles
    const newParticles = Array.from({ length: 20 }, (_, i) => ({
      id: i,
      size: Math.random() * 3 + 1,
      left: Math.random() * 100,
      animationDelay: Math.random() * 20,
      duration: Math.random() * 15 + 10,
      opacity: Math.random() * 0.6 + 0.2
    }));
    setParticles(newParticles);

    // Create floating orbs
    const newOrbs = Array.from({ length: 8 }, (_, i) => ({
      id: i,
      size: Math.random() * 150 + 50,
      left: Math.random() * 100,
      top: Math.random() * 100,
      animationDelay: Math.random() * 10,
      duration: Math.random() * 20 + 15
    }));
    setOrbs(newOrbs);

    // Mouse move handler
    const handleMouseMove = (e) => {
      setMousePosition({
        x: (e.clientX / window.innerWidth) * 100,
        y: (e.clientY / window.innerHeight) * 100
      });
    };

    window.addEventListener("mousemove", handleMouseMove);
    return () => window.removeEventListener("mousemove", handleMouseMove);
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);

    // Simulate API call
    await new Promise((resolve) => setTimeout(resolve, 1500));

    const userData = {
      name: email.split("@")[0],
      email,
      avatar: `https://ui-avatars.com/api/?name=${encodeURIComponent(
        email.split("@")[0]
      )}&background=3B82F6&color=fff`
    };

    console.log("Login successful:", userData);

    setIsLoading(false);

    // ✅ Redirect to "For You" page
    navigate("/foryou");
  };

  const togglePasswordVisibility = () => {
    setShowPassword(!showPassword);
  };

  const isFormValid = email && password;
  const [isEyeHovered, setIsEyeHovered] = useState(false);

  // ⬇️ KEEPING your exact styles
  const styles = { /* Your existing styles from your previous code */ };

  // SVG Icons
  const EmailIcon = () => (
    <svg width="18" height="18" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
      <path d="M4 4h16c1.1 0 2 .9 2 2v12c0 1.1-.9 2-2 2H4c-1.1 0-2-.9-2-2V6c0-1.1.9-2 2-2z"/>
      <polyline points="22,6 12,13 2,6"/>
    </svg>
  );

  const EyeIcon = () => (
    <svg width="18" height="18" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
      <path d="M1 12s4-8 11-8 11 8 11 8-4 8-11 8-11-8-11-8z"/>
      <circle cx="12" cy="12" r="3"/>
    </svg>
  );

  const EyeOffIcon = () => (
    <svg width="18" height="18" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
      <path d="M17.94 17.94A10.07 10.07 0 0 1 12 20c-7 0-11-8-11-8a18.45 18.45 0 0 1 5.06-5.94M9.9 4.24A9.12 9.12 0 0 1 12 4c7 0 11 8 11 8a18.5 18.5 0 0 1-2.16 3.19m-6.72-1.07a3 3 0 1 1-4.24-4.24"/>
      <line x1="1" y1="1" x2="23" y2="23"/>
    </svg>
  );

  const LockIcon = () => (
    <svg width="18" height="18" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
      <rect x="3" y="11" width="18" height="11" rx="2" ry="2"/>
      <path d="M7 11V7a5 5 0 0 1 10 0v4"/>
    </svg>
  );

  return (
    <div style={styles.container}>
      {/* Background Effects */}
      <div style={styles.backgroundOverlay} />
      <div style={styles.meshGradient} />

      {/* Floating Orbs */}
      <div style={styles.particlesContainer}>
        {orbs.map(orb => (
          <div
            key={`orb-${orb.id}`}
            style={{
              ...styles.orb,
              width: `${orb.size}px`,
              height: `${orb.size}px`,
              left: `${orb.left}%`,
              top: `${orb.top}%`,
              background: orb.id % 3 === 0 
                ? "radial-gradient(circle, #60a5fa, #3b82f6)"
                : orb.id % 3 === 1 
                ? "radial-gradient(circle, #a855f7, #8b5cf6)"
                : "radial-gradient(circle, #ec4899, #f43f5e)",
              animationDelay: `${orb.animationDelay}s`,
              animationDuration: `${orb.duration}s`
            }}
          />
        ))}
      </div>

      {/* Animated Particles */}
      <div style={styles.particlesContainer}>
        {particles.map(particle => (
          <div
            key={particle.id}
            style={{
              ...styles.particle,
              width: `${particle.size}px`,
              height: `${particle.size}px`,
              left: `${particle.left}%`,
              top: "-20px",
              opacity: particle.opacity,
              animationDelay: `${particle.animationDelay}s`,
              animationDuration: `${particle.duration}s`
            }}
          />
        ))}
      </div>

      {/* Login Card */}
      <div style={styles.card}>
        <div style={styles.cardGlow} />

        <div style={styles.logo}>
          <h1 style={styles.logoText}>TaskFlow</h1>
          <p style={styles.tagline}>Organize. Track. Deliver.</p>
        </div>

        <h2 style={styles.title}>Login</h2>

        <form onSubmit={handleSubmit}>
          {/* Email Field */}
          <div style={styles.inputGroup}>
            <label style={styles.label}>Email</label>
            <div
              style={{
                ...styles.inputContainer,
                ...(activeInput === "email" ? styles.inputFocus : {})
              }}
            >
              <input
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                onFocus={() => setActiveInput("email")}
                onBlur={() => setActiveInput(null)}
                style={{
                  ...styles.input,
                  ...(activeInput === "email" ? styles.inputFocus : {})
                }}
                placeholder="Enter your email"
                required
              />
              <div style={styles.inputIcon}>
                <EmailIcon />
              </div>
            </div>
          </div>

          {/* Password Field */}
          <div style={styles.inputGroup}>
            <label style={styles.label}>Password</label>
            <div
              style={{
                ...styles.inputContainer,
                ...(activeInput === "password" ? styles.inputFocus : {})
              }}
            >
              <input
                type={showPassword ? "text" : "password"}
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                onFocus={() => setActiveInput("password")}
                onBlur={() => setActiveInput(null)}
                style={{
                  ...styles.input,
                  ...(activeInput === "password" ? styles.inputFocus : {})
                }}
                placeholder="Enter your password"
                required
              />
              <button
                type="button"
                onClick={togglePasswordVisibility}
                onMouseEnter={() => setIsEyeHovered(true)}
                onMouseLeave={() => setIsEyeHovered(false)}
                style={{
                  ...styles.eyeIcon,
                  ...(isEyeHovered ? styles.eyeIconHover : {})
                }}
              >
                {showPassword ? <EyeOffIcon /> : <EyeIcon />}
              </button>
            </div>
          </div>

          {/* Submit Button */}
          <button
            type="submit"
            disabled={!isFormValid || isLoading}
            style={{
              ...styles.button,
              ...(isHovered ? styles.buttonHover : {}),
              ...(!isFormValid || isLoading ? styles.buttonDisabled : {})
            }}
            onMouseEnter={() => setIsHovered(true)}
            onMouseLeave={() => setIsHovered(false)}
          >
            {isLoading ? (
              <div style={styles.loadingSpinner}></div>
            ) : (
              <>
                Sign In
                <div style={styles.buttonGlow}></div>
              </>
            )}
          </button>
        </form>

        <div style={styles.footer}>
          <p style={styles.footerText}>
            Don’t have an account?{" "}
            <a
              href="/signup"
              style={styles.link}
              onMouseEnter={(e) => (e.target.style.color = styles.linkHover.color)}
              onMouseLeave={(e) => (e.target.style.color = styles.link.color)}
            >
              Sign up
            </a>
          </p>
        </div>
      </div>

      {/* Animations */}
      <style>
        {`
          @keyframes floatParticles {0% { transform: translateY(0); }100% { transform: translateY(110vh); }}
          @keyframes floatOrbs {0% { transform: translateY(0) translateX(0); }100% { transform: translateY(60px) translateX(40px); }}
          @keyframes rotateMesh {0% { transform: rotate(0deg); }100% { transform: rotate(360deg); }}
          @keyframes slideUp {0% { opacity: 0; transform: translateY(40px); }100% { opacity: 1; transform: translateY(0); }}
          @keyframes cardGlow {0%, 100% { transform: translateX(-100%); }50% { transform: translateX(100%); }}
          @keyframes fadeIn {from { opacity: 0; transform: translateY(10px); } to { opacity: 1; transform: translateY(0); }}
          @keyframes textShimmer {0%, 100% { opacity: 1; filter: brightness(1); }50% { opacity: 0.9; filter: brightness(1.2); }}
          @keyframes spin { to { transform: rotate(360deg); } }
        `}
      </style>
    </div>
  );
}

export default LoginPage;
