			} else if (mapTo == "start_date") {
				item.duration = gantt.calculateDuration(item);
			}
		} else {
			if (mapTo == "start_date" || mapTo == "duration") {
				item.end_date = gantt.calculateEndDate(item);
			} else if (mapTo == "end_date") {
