import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../../context/AuthContext";

function LoginPage() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const { login } = useAuth();
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);

    // Simulate API call delay
    await new Promise(resolve => setTimeout(resolve, 1000));

    // Fake authentication (replace with API later)
    const userData = {
      name: email.split("@")[0],
      email,
      avatar: `https://ui-avatars.com/api/?name=${encodeURIComponent(email.split("@")[0])}&background=0052CC&color=fff`
    };

    login(userData);
    navigate("/for-you");
    setIsLoading(false);
  };

  const styles = {
    container: {
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
      minHeight: "100vh",
      backgroundColor: "#F4F5F7",
      backgroundImage: "linear-gradient(135deg, #0052CC 0%, #0747A6 100%)",
      padding: "20px",
      fontFamily: "'Segoe UI', -apple-system, BlinkMacSystemFont, sans-serif"
    },
    card: {
      backgroundColor: "#FFFFFF",
      padding: "40px",
      borderRadius: "8px",
      boxShadow: "0 8px 32px rgba(9, 30, 66, 0.25)",
      width: "100%",
      maxWidth: "400px",
      border: "1px solid #DFE1E6"
    },
    logo: {
      textAlign: "center",
      marginBottom: "32px"
    },
    logoText: {
      fontSize: "32px",
      fontWeight: "700",
      color: "#0052CC",
      margin: "0",
      background: "linear-gradient(135deg, #0052CC 0%, #0747A6 100%)",
      WebkitBackgroundClip: "text",
      WebkitTextFillColor: "transparent",
      backgroundClip: "text"
    },
    tagline: {
      textAlign: "center",
      color: "#6B778C",
      fontSize: "14px",
      marginTop: "4px",
      fontWeight: "400"
    },
    title: {
      textAlign: "center",
      marginBottom: "32px",
      fontSize: "24px",
      fontWeight: "600",
      color: "#172B4D",
      marginTop: "0"
    },
    inputGroup: {
      marginBottom: "20px",
      position: "relative"
    },
    label: {
      display: "block",
      marginBottom: "6px",
      fontSize: "14px",
      fontWeight: "600",
      color: "#172B4D"
    },
    input: {
      width: "100%",
      padding: "12px",
      borderRadius: "4px",
      border: "2px solid #DFE1E6",
      fontSize: "14px",
      color: "#172B4D",
      backgroundColor: "#FAFBFC",
      transition: "all 0.2s ease",
      boxSizing: "border-box"
    },
    inputFocus: {
      outline: "none",
      borderColor: "#0052CC",
      backgroundColor: "#FFFFFF",
      boxShadow: "0 0 0 2px rgba(0, 82, 204, 0.2)"
    },
    button: {
      width: "100%",
      padding: "12px 16px",
      border: "none",
      borderRadius: "4px",
      backgroundColor: "#0052CC",
      color: "#FFFFFF",
      fontWeight: "600",
      fontSize: "14px",
      cursor: "pointer",
      transition: "all 0.2s ease",
      position: "relative",
      height: "40px"
    },
    buttonHover: {
      backgroundColor: "#0747A6",
      transform: "translateY(-1px)"
    },
    buttonDisabled: {
      backgroundColor: "#B3D4FF",
      cursor: "not-allowed",
      transform: "none"
    },
    loadingSpinner: {
      border: "2px solid #f3f3f3",
      borderTop: "2px solid #0052CC",
      borderRadius: "50%",
      width: "16px",
      height: "16px",
      animation: "spin 1s linear infinite",
      margin: "0 auto"
    },
    footer: {
      textAlign: "center",
      marginTop: "24px",
      paddingTop: "24px",
      borderTop: "1px solid #DFE1E6"
    },
    footerText: {
      color: "#6B778C",
      fontSize: "12px",
      margin: "0"
    },
    error: {
      color: "#DE350B",
      fontSize: "12px",
      marginTop: "4px",
      display: "none" // Hidden for now, can be shown with validation
    },
    link: {
      color: "#0052CC",
      textDecoration: "none",
      fontWeight: "500"
    }
  };

  const isFormValid = email && password;

  return (
    <div style={styles.container}>
      <div style={styles.card}>
        {/* Logo Section */}
        <div style={styles.logo}>
          <h1 style={styles.logoText}>Flow Track</h1>
          <p style={styles.tagline}>Streamline your workflow, track your progress</p>
        </div>

        <h2 style={styles.title}>Sign in to your account</h2>

        <form onSubmit={handleSubmit}>
          {/* Email Input */}
          <div style={styles.inputGroup}>
            <label style={styles.label}>Email address</label>
            <input
              type="email"
              placeholder="Enter your email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              style={{
                ...styles.input,
                ...(email ? styles.inputFocus : {})
              }}
              required
            />
          </div>

          {/* Password Input */}
          <div style={styles.inputGroup}>
            <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
              <label style={styles.label}>Password</label>
              <a href="#" style={{ ...styles.link, fontSize: "12px" }}>Forgot password?</a>
            </div>
            <input
              type="password"
              placeholder="Enter your password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              style={{
                ...styles.input,
                ...(password ? styles.inputFocus : {})
              }}
              required
            />
          </div>

          {/* Submit Button */}
          <button 
            type="submit" 
            style={{
              ...styles.button,
              ...(isFormValid && !isLoading ? { ...styles.buttonHover } : {}),
              ...(!isFormValid || isLoading ? styles.buttonDisabled : {})
            }}
            disabled={!isFormValid || isLoading}
          >
            {isLoading ? (
              <div style={styles.loadingSpinner}></div>
            ) : (
              "Sign in"
            )}
          </button>
        </form>

        {/* Footer */}
        <div style={styles.footer}>
          <p style={styles.footerText}>
            Don't have an account?{" "}
            <a href="#" style={styles.link}>Contact administrator</a>
          </p>
          <p style={{ ...styles.footerText, marginTop: "8px" }}>
            © 2024 Flow Track. Jira-inspired project management.
          </p>
        </div>
      </div>

      {/* Add CSS animation for spinner */}
      <style>
        {`
          @keyframes spin {
            0% { transform: rotate(0deg); }
            100% { transform: rotate(360deg); }
          }
          input:focus {
            outline: none;
            border-color: #0052CC !important;
            background-color: #FFFFFF !important;
            box-shadow: 0 0 0 2px rgba(0, 82, 204, 0.2) !important;
          }
          button:hover:not(:disabled) {
            background-color: #0747A6 !important;
            transform: translateY(-1px) !important;
          }
        `}
      </style>
    </div>
  );
}

export default LoginPage;
