import React, { useEffect, useState } from 'react';

export default function ForYou() {
  const [user, setUser] = useState(null);
  const [recentlyViewed, setRecentlyViewed] = useState([]);
  const [yourBoards, setYourBoards] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Initialize empty data structures
    setRecentlyViewed([]);
    setYourBoards([]);

    // Simulate API call to backend
    const fetchData = async () => {
      try {
        setLoading(true);

        // TODO: Replace with actual API calls
        // For now, set empty user and simulate loading delay
        setUser({ name: "" }); // Empty name to be filled from backend
        setTimeout(() => {
          setLoading(false);
        }, 1000);

      } catch (error) {
        console.error('Error fetching data:', error);
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const styles = {
    container: {
      minHeight: "100vh",
      background: "linear-gradient(135deg, #667eea 0%, #764ba2 100%)",
      padding: "2rem",
      fontFamily: "'Inter', -apple-system, BlinkMacSystemFont, sans-serif",
      position: "relative",
      overflow: "hidden"
    },
    content: {
      position: "relative",
      zIndex: 10,
      maxWidth: "1200px",
      margin: "0 auto"
    },
    welcomeTitle: {
      fontSize: "2.5rem",
      fontWeight: "700",
      color: "#FFFFFF",
      margin: "0 0 0.5rem 0",
      animation: "fadeInUp 1s ease-out",
      textShadow: "0 2px 10px rgba(0,0,0,0.2)"
    },
    subtitle: {
      color: "rgba(255, 255, 255, 0.9)",
      fontSize: "1.1rem",
      fontWeight: "400",
      margin: "0 0 3rem 0",
      animation: "fadeInUp 1.2s ease-out"
    },
    cardsContainer: {
      display: "grid",
      gridTemplateColumns: "1fr 1fr",
      gap: "2rem",
      marginBottom: "2rem"
    },
    card: {
      background: "rgba(255, 255, 255, 0.95)",
      backdropFilter: "blur(10px)",
      borderRadius: "16px",
      padding: "2rem",
      border: "1px solid rgba(255, 255, 255, 0.3)",
      boxShadow: "0 8px 32px rgba(0, 0, 0, 0.1)",
      position: "relative",
      animation: "fadeInUp 1.4s ease-out"
    },
    cardHeader: {
      display: "flex",
      justifyContent: "space-between",
      alignItems: "center",
      marginBottom: "1.5rem"
    },
    cardTitle: {
      fontSize: "1.25rem",
      fontWeight: "600",
      color: "#2D3748",
      margin: "0"
    },
    boardList: {
      listStyle: "none",
      padding: "0",
      margin: "0",
      display: "flex",
      flexDirection: "column",
      gap: "0.75rem"
    },
    boardItem: {
      padding: "1rem",
      background: "rgba(255, 255, 255, 0.8)",
      borderRadius: "8px",
      border: "1px solid rgba(255, 255, 255, 0.5)",
      transition: "all 0.2s ease",
      cursor: "pointer"
    },
    boardTitle: {
      fontSize: "0.95rem",
      fontWeight: "500",
      color: "#2D3748",
      margin: "0 0 0.25rem 0",
      display: "flex",
      alignItems: "center",
      gap: "0.5rem"
    },
    boardMeta: {
      display: "flex",
      justifyContent: "space-between",
      alignItems: "center",
      fontSize: "0.75rem",
      color: "#718096"
    },
    teamBadge: {
      padding: "0.2rem 0.5rem",
      borderRadius: "6px",
      fontSize: "0.7rem",
      fontWeight: "500",
      background: "rgba(102, 126, 234, 0.1)",
      color: "#667eea"
    },
    itemsCount: {
      padding: "0.2rem 0.5rem",
      borderRadius: "6px",
      fontSize: "0.7rem",
      fontWeight: "500",
      background: "rgba(102, 126, 234, 0.1)",
      color: "#667eea"
    },
    colorIndicator: {
      width: "3px",
      height: "16px",
      borderRadius: "2px"
    },
    emptyState: {
      textAlign: "center",
      color: "#A0AEC0",
      padding: "2rem 1rem",
      fontSize: "0.9rem"
    },
    loadingSpinner: {
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
      padding: "3rem",
      color: "#FFFFFF"
    },
    spinner: {
      width: "40px",
      height: "40px",
      border: "4px solid rgba(255, 255, 255, 0.3)",
      borderLeft: "4px solid #FFFFFF",
      borderRadius: "50%",
      animation: "spin 1s linear infinite"
    }
  };

  if (loading) {
    return (
      <div style={styles.container}>
        <div style={styles.loadingSpinner}>
          <div style={styles.spinner}></div>
        </div>

        <style>
          {`
            @keyframes spin {
              0% { transform: rotate(0deg); }
              100% { transform: rotate(360deg); }
            }
          `}
        </style>
      </div>
    );
  }

  return (
    <div style={styles.container}>
      <div style={styles.content}>
        {/* Header */}
        <h1 style={styles.welcomeTitle}>
          üëã Welcome{user?.name ? `, ${user.name}` : ''}
        </h1>
        <p style={styles.subtitle}>Your workspace at a glance</p>

        {/* Two Column Layout */}
        <div style={styles.cardsContainer}>
          {/* Recently Viewed Card */}
          <div style={styles.card}>
            <div style={styles.cardHeader}>
              <h2 style={styles.cardTitle}>üïí Recently Viewed</h2>
            </div>
            <ul style={styles.boardList}>
              {recentlyViewed.length > 0 ? (
                recentlyViewed.map((board) => (
                  <li key={board.id} style={styles.boardItem} className="board-item">
                    <div style={styles.boardTitle}>
                      <div 
                        style={{
                          ...styles.colorIndicator,
                          backgroundColor: board.color || "#667eea"
                        }} 
                      />
                      {board.title}
                    </div>
                    <div style={styles.boardMeta}>
                      <span style={styles.teamBadge}>{board.team || "Team"}</span>
                      <span>Viewed {board.lastViewed || "recently"}</span>
                    </div>
                  </li>
                ))
              ) : (
                <li style={styles.emptyState}>No recently viewed boards</li>
              )}
            </ul>
          </div>

          {/* Your Boards Card */}
          <div style={styles.card}>
            <div style={styles.cardHeader}>
              <h2 style={styles.cardTitle}>üóÇÔ∏è Your Boards</h2>
            </div>
            <ul style={styles.boardList}>
              {yourBoards.length > 0 ? (
                yourBoards.map((board) => (
                  <li key={board.id} style={styles.boardItem} className="board-item">
                    <div style={styles.boardTitle}>
                      <div 
                        style={{
                          ...styles.colorIndicator,
                          backgroundColor: board.color || "#667eea"
                        }} 
                      />
                      {board.title}
                    </div>
                    <div style={styles.boardMeta}>
                      <span style={styles.itemsCount}>
                        {board.items || 0} items
                      </span>
                      <span>Updated {board.updated || "recently"}</span>
                    </div>
                  </li>
                ))
              ) : (
                <li style={styles.emptyState}>No boards created yet</li>
              )}
            </ul>
          </div>
        </div>
      </div>

      {/* CSS Animations */}
      <style>
        {`
          @keyframes fadeInUp {
            0% {
              opacity: 0;
              transform: translateY(20px);
            }
            100% {
              opacity: 1;
              transform: translateY(0);
            }
          }

          .board-item:hover {
            background: rgba(255, 255, 255, 0.95) !important;
            transform: translateY(-1px) !important;
            box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1) !important;
          }
        `}
      </style>
    </div>
  );
}
