import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { useProjects } from "../../context/ProjectContext";

export default function ProjectView() {
  const { id } = useParams(); // undefined if creating new
  const navigate = useNavigate();
  const { projects, addProject, getProjectById } = useProjects();

  const [project, setProject] = useState(null);
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");

  useEffect(() => {
    if (id && id !== "new") {
      const p = getProjectById(id);
      setProject(p);
    }
  }, [id, projects]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!name) return;
    const newProject = addProject({ name, description });
    navigate(`/projects/${newProject.id}`);
  };

  if (id === "new") {
    return (
      <div style={{ padding: "2rem", fontFamily: "Segoe UI, sans-serif" }}>
        <h1>üìÅ Create New Project</h1>
        <form onSubmit={handleSubmit} style={{ marginTop: "1rem" }}>
          <div style={{ marginBottom: "1rem" }}>
            <label>Project Name:</label>
            <input type="text" value={name} onChange={(e) => setName(e.target.value)} style={{ width: "100%", padding: "0.5rem", marginTop: "0.3rem" }} />
          </div>
          <div style={{ marginBottom: "1rem" }}>
            <label>Description:</label>
            <textarea value={description} onChange={(e) => setDescription(e.target.value)} style={{ width: "100%", padding: "0.5rem", marginTop: "0.3rem" }} />
          </div>
          <button type="submit" style={{ padding: "0.5rem 1rem", background: "#10b981", color: "#fff", border: "none", borderRadius: "6px" }}>Create Project</button>
        </form>
      </div>
    );
  }

  if (!project) return <p style={{ padding: "2rem" }}>No project found.</p>;

  return (
    <div style={{ padding: "2rem", fontFamily: "Segoe UI, sans-serif" }}>
      <h1>üìÅ {project.name}</h1>
      <p>{project.description}</p>
    </div>
  );
}
